/// <summary>
/// Set the AccountDimension button's display menu item to point to the correct form.
/// Set the OffsetAccountDimension button's display menu item to point to the correct form.
/// Set the PaymentFeeAccountDimension button's display menu item to point to the correct form.
/// </summary>
/// <remarks>
/// Display menuitem name changed depending on the caller menuitem of this form.
/// </remarks>
public void enableButtons()
{
    if (element.args())
    {
        switch (element.args().menuItemName())
        {
            case menuitemDisplayStr(CustPaymReconciliation):
                accountDimension.menuItemName(menuitemDisplayStr(LedgerJourTransDimCustPaymRecon));
                offsetAccountDimension.menuItemName(menuitemDisplayStr(LedgerJourTrOffDimCustPaymRecon));
                paymentFeeAccountDimension.menuItemName(menuitemDisplayStr(LedgerJourTrPayFeeDimCustPaymRecon));
                break;

            case menuitemDisplayStr(VendPaymReconciliation):
                accountDimension.menuItemName(menuitemDisplayStr(LedgerJourTransDimVendPaymRecon));
                offsetAccountDimension.menuItemName(menuitemDisplayStr(LedgerJourTrOffDimVendPaymRecon));
                paymentFeeAccountDimension.menuItemName(menuitemDisplayStr(LedgerJourTrPayFeeDimVendPaymRecon));
                break;

            default:
                accountDimension.enabled(false);
                offsetAccountDimension.enabled(false);
                paymentFeeAccountDimension.enabled(false);
                break;
        }
    }
}

