private void enableLedgerJournalTransFields()
{
    boolean canModifyExchangeRate = ledgerJournalEngine.mayCurrencyBeModified(ledgerJournalTrans);
    boolean canModifyTransactionDate = !ledgerJournalTrans.isDependantOnSettlement();

    editExchRate.allowEdit(canModifyExchangeRate);
    ledgerJournalTrans_TransDate.allowEdit(canModifyTransactionDate);
    identification_TransDate.allowEdit(canModifyTransactionDate);
}

private void setErrorCodeDescription()
{
    errorCodeDescription = '';

    if (LedgerJournalTrans.ErrorCodePayment)
    {
        BankReturnFormatErrorCodes_W returnCode;
        select firstonly Description from returnCode
            where returnCode.Code == LedgerJournalTrans.ErrorCodePayment;

        if (returnCode)
        {
            errorCodeDescription = returnCode.Description;
            GlobalizationInstrumentationHelper::featureRun(FeatureReference, funcName());
        }
    }
}

