}

boolean validateWrite()
{
    boolean ret = true;

    if (ledgerJournalTable.SystemBlocked)
    {
        ret = checkFailed(strFmt("@SYS68942", ledgerJournalTable.JournalNum)) && ret;
        ledgerJournalTrans.reread();
    }
    if (ledgerJournalTable.Posted)
    {
        ret = checkFailed(strFmt("@SYS68943", ledgerJournalTable.JournalNum)) && ret;
        ledgerJournalTrans.reread();
    }

    ret = ret && super();

    return ret;
}

void init()
{
    #ISOCountryRegionCodes

    super();

    QueryBuildDataSource qbds = this.query().dataSourceTable(tableNum(LedgerJournalTrans));

    QueryBuildRange invisiblePaymentRange = SysQuery::findOrCreateRange(qbds, fieldNum(LedgerJournalTrans, Invisible));
    invisiblePaymentRange.value(queryValue(NoYes::No));
    invisiblePaymentRange.status(RangeStatus::Locked);

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoNO]))
    {
        QueryBuildRange paymentStatusRange = SysQuery::findOrCreateRange(qbds, fieldNum(LedgerJournalTrans, PaymentStatus));

        paymentStatusRange.value(strFmt(
            '%1,%2,%3,%4,%5,%6',
            CustVendPaymStatus::Sent,
            CustVendPaymStatus::Recieved,
            CustVendPaymStatus::Confirmed,
            CustVendPaymStatus::Rejected,
            CustVendPaymStatus::SentDelete,
            CustVendPaymStatus::Deleted));

        paymentStatusRange.status(RangeStatus::Locked);
    }
    else
    {
        QueryBuildRange paymentStatusRange = SysQuery::findOrCreateRange(qbds, fieldNum(LedgerJournalTrans, PaymentStatus));

        paymentStatusRange.value(strFmt(
            '%1,%2,%3,%4',
            CustVendPaymStatus::Sent,
            CustVendPaymStatus::Recieved,
            CustVendPaymStatus::Confirmed,
            CustVendPaymStatus::Rejected));

        paymentStatusRange.status(RangeStatus::Locked);
    }

    switch (moduleCustVend)
    {
        case ModuleCustVend::Vend:
            ledgerJournalEngine = new LedgerJournalEngine_VendPayment(element);
            break;

        case ModuleCustVend::Cust:
            ledgerJournalEngine = new LedgerJournalEngine_CustPayment(element);
            break;

        default:
            throw error("@SYS22996");
    }

    this.cacheAddMethod(tableMethodStr(LedgerJournalTrans, accountName));
}

