public void calcConfirmed_SE()
{
    confirmedAmount_SE  = 0;
    confirmedCounter_SE = 0;

    ledgerJournalTrans_ds.first();

    QueryRun qr = new QueryRun(ledgerJournalTrans_q);

    while (qr.next())
    {
        LedgerJournalTrans ledgerJournalTransLocal = qr.get(tableNum(LedgerJournalTrans));

        if (ledgerJournalTransLocal.PaymentStatus == CustVendPaymStatus::Confirmed)
        {
            confirmedCounter_SE++;

            if (ledgerJournalTransLocal.AmountCurDebit)
            {
                confirmedAmount_SE += ledgerJournalTransLocal.calcDebitMST();
            }
            else
            {
                confirmedAmount_SE -= ledgerJournalTransLocal.calcCreditMST();
            }
        }
    }
}

public void executeQuery()
{
    #ISOCountryRegionCodes

    super();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoSE]))
    {
        this.calcConfirmed_SE();
    }
}

