void init()
{
    super();

    QueryBuildDataSource ledgerJournalTableQBDS = this.query().dataSourceTable(tableNum(LedgerJournalTable));

    QueryBuildRange postedRange = SysQuery::findOrCreateRange(ledgerJournalTableQBDS, fieldNum(LedgerJournalTable, Posted));
    postedRange.value(enum2str(NoYes::No));
    postedRange.status(RangeStatus::Locked);

    QueryBuildRange journalTypeRange = SysQuery::findOrCreateRange(ledgerJournalTableQBDS, fieldNum(LedgerJournalTable, JournalType));
    journalTypeRange.status(RangeStatus::Locked);

    switch (moduleCustVend)
    {
        case ModuleCustVend::Vend:
            journalTypeRange.value(enum2str(LedgerJournalType::Payment));

            vendPaymReconciliationImport.visible(true);
            custShowSpecifikation.visible(false);
            break;

        case ModuleCustVend::Cust:
            journalTypeRange.value(enum2str(LedgerJournalType::CustPayment));

            custPaymReconciliationImport.visible(true);
            vendShowSpecifikation.visible(false);
            break;
    }
}

public void modifyModuleAccountType()
{
    // Clear the account
    custVendPaymJournalFee.parmLedgerDimension(0);

    if (custVendPaymJournalFee.LedgerJournalACType == LedgerJournalACType::Ledger ||
        (custVendPaymJournalFee.LedgerJournalACType == LedgerJournalACType::Cust &&
         custVendPaymJournalFee.Module == ModuleCustVend::Cust) ||
        (custVendPaymJournalFee.LedgerJournalACType == LedgerJournalACType::Vend &&
         custVendPaymJournalFee.Module == ModuleCustVend::Vend))
    {
        // Enable account if the account type is ledger or the module & account type are customer or
        // the module & account type are vendor
        custVendPaymJournalFee_CustVendAC.enabled(true);
    }
    else
    {
        custVendPaymJournalFee_CustVendAC.enabled(false);
    }
}

