    if (custVendPaymJournalFee.LedgerJournalACType != LedgerJournalACType::Ledger)
    {
        paymentFeeAccountDimension.enabled(true);
    }
    else
    {
        paymentFeeAccountDimension.enabled(false);
    }

    super();
}

