[Form]
public class CustVendPrepaymentHandling_W extends FormRun implements IFormRefreshable
{
    CustVendTrans                       custVendTrans;
    [FormObservable]
    TaxAmount                           correctedTaxAmountCur;
    CustVendPrepaymentHandling_W        custVendPrepaymentHandling;

    #ISOCountryRegionCodes

}

public void closeOk()
{
    FormDataSource formDataSource;
    FormDataSource mainDataSource;

    // <GEECZ>
    custVendPrepaymentHandling.parmTaxDocumentDate(taxDocumentDate.dateValue());
    custVendPrepaymentHandling.parmTaxDocumentNum(taxDocumentNum.text());
    // </GEECZ>

    custVendPrepaymentHandling.parmConversion(conversion.value());
    custVendPrepaymentHandling.parmVendorVATDate(LedgerJournalTrans.VendorVATDate);

    if (! custVendPrepaymentHandling.check())
    {
        return;
    }

    custVendPrepaymentHandling.run();

    super();

    formDataSource = FormDataUtil::getFormDataSource(element.args().record());

    if (formDataSource && formDataSource.formRun() && !formDataSource.formRun().closed())
    {
        if (custVendTrans.TableId == tablenum(CustTrans))
        {
            custVendTrans = CustTrans::find(custVendTrans.RecId);
        }
        else
        {
            custVendTrans = VendTrans::find(custVendTrans.RecId);
        }

        if (formDataSource.formRun().dataSource())
        {
            mainDataSource = formDataSource.formRun().dataSource();
            mainDataSource.executeQuery();
        }
        else
        {
            formDataSource.executeQuery();
        }

        formDataSource.positionToRecord(custVendTrans);
    }
}

public void init()
{
    super();

    if (element.args().dataset() != tablenum(CustTrans) &&
        element.args().dataset() != tablenum(VendTrans))
    {
        throw error("@GLS106133");
    }

    ledgerJournalTrans.setTmp();

    custVendTrans = CustVendTransStatic::construct(element.args().record()).custVendTrans();
    custVendTrans.data(element.args().record());
    custVendPrepaymentHandling = CustVendPrepaymentHandling_W::newFromCustVendTrans(custVendTrans);
    custVendPrepaymentHandling.init();
    custVendPrepaymentHandling.initLedgerJournalEngine();
    ledgerJournalTrans.setTmpData(custVendPrepaymentHandling.tmpLedgerJournalTrans());

    element.design().caption(custVendPrepaymentHandling.formDesignCaption());

    if (custVendTrans.TableId == tablenum(CustTrans))
    {
        custPostingProfile.visible(custVendPrepaymentHandling.showPostingProfile());
        ledgerJournalTrans_AmountCurCredit.visible(true);
        custAdvanceInvoiceProposal.visible(custVendPrepaymentHandling.showAdvanceInvoiceProposal());
        custAdvanceInvoiceProposal.enabled(! custVendPrepaymentHandling.parmConversion());
        factureAutocreate.visible(!custVendTrans.Prepayment);
    }
    else
    {
        vendPostingProfile.visible(custVendPrepaymentHandling.showPostingProfile());
        ledgerJournalTrans_AmountCurDebit.visible(true);
        custAdvanceInvoiceProposal.visible(false);
    }

    conversion.value(custVendPrepaymentHandling.parmConversion());
    conversion.visible(custVendPrepaymentHandling.showConversion());
    ledgerJournalTrans_ds.allowEdit(! custVendPrepaymentHandling.parmConversion());
    ledgerJournalTrans_ds.allowDelete(! custVendPrepaymentHandling.parmConversion());
    ledgerJournalTrans_ds.allowCreate(! custVendPrepaymentHandling.parmConversion());

    // <GEECZ>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoCZ]))
    {
        taxDocument.allowEdit(custVendPrepaymentHandling.allowEditTaxDocument());
        taxDocument.visible(custVendPrepaymentHandling.showTaxDocument());

        taxCreditMemo.allowEdit(custVendPrepaymentHandling.allowEditTaxCreditMemo());
        taxCreditMemo.visible(custVendPrepaymentHandling.showTaxCreditMemo());

        taxDocumentDate.visible(taxDocument.visible() || taxCreditMemo.visible());
        taxDocumentDate.allowEdit(taxDocument.allowEdit()                            ||
                                  taxCreditMemo.allowEdit()                          ||
                                  custVendPrepaymentHandling.parmCreateTaxDocument() ||
                                  custVendPrepaymentHandling.parmCreateTaxCreditMemo());

        taxDocumentNum.visible(custVendPrepaymentHandling.showTaxDocumentNum());
        taxDocumentNum.allowEdit(taxDocumentDate.allowEdit());
    }
    // </GEECZ>

    tab.visible(custVendPrepaymentHandling.showTab());

    transDate.dateValue(custVendPrepaymentHandling.parmTransDate());
    transDate.visible(custVendPrepaymentHandling.showTransDate());

    prepaymentAmountCur.visible(custVendPrepaymentHandling.showPrepaymentAmount());
    remainAmountCur.visible(custVendPrepaymentHandling.showRemainAmount());

    buttonCheckJournal.visible(custVendPrepaymentHandling.showButtons());
    taxTransSource.visible(custVendPrepaymentHandling.showButtonSalesTax());

    LedgerJournalTrans_AccountNum.parmDimensionAutocompleteFilter(new DimensionAutocompleteVendDefaultFilter(ledgerJournalTrans_ds, fieldStr(LedgerJournalTrans, OffsetLedgerDimension), fieldStr(LedgerJournalTrans, OffsetAccountType)));

    LedgerJournalTrans_AccountNum.parmPostingType(LedgerPostingType::LedgerJournal);
    LedgerJournalTrans_AccountNum.parmValidateBlockedForManualEntry(true);
}

/// <summary>
/// Skip tax calculation for tax integration scenario when the button 'Transform to payment' is on.
/// </summary>
private boolean shouldSkipTaxCalculation()
{
    return conversion.checked()
        && custVendPrepaymentHandling.paramTaxIntegrationEnabledForJournal()
        && TaxIntegrationPrepaymentHandlingFormFlight::instance().isEnabled();
}

public void write()
{
    // <GEEPL>
    RefRecId serviceTariffNumber;

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
    {
        serviceTariffNumber = taxServiceTariff.ServiceTariffNumber;
    }
    // </GEEPL>

    super();

    // <GEEPL>
    // The service tariff relationship is lost during the super() call
    // from write since it is on a different data source. So, copy
    // the original value back in to ensure saving is done correctly.
    if (serviceTariffNumber && SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
    {
        TaxServiceTariff.ServiceTariffNumber = serviceTariffNumber;
        TaxServiceTariff.ParentRecId = ledgerJournalTrans.RecId;
        TaxServiceTariff.ParentTableId = ledgerJournalTrans.TableId;
    }
    // </GEEPL>

    custVendPrepaymentHandling.ledgerJournalTrans(ledgerJournalTrans);
}

public int active()
{
    int ret = super();

    LedgerJournalTrans_AccountNum.parmCurrency(ledgerJournalTrans.CurrencyCode);
    LedgerJournalTrans_AccountNum.parmControlDate(ledgerJournalTrans.TransDate);

    this.reread();

    return ret;
}

display TaxAmountJournal correctedTaxAmount(LedgerJournalTrans _ledgerJournalTrans)
{
    TaxAmountCur   taxAmountCur;
    if (!element.shouldSkipTaxCalculation())
    {
        if (_ledgerJournalTrans.RecId)
        {
            taxAmountCur = custVendPrepaymentHandling.correctedTaxAmount(_ledgerJournalTrans);
        }
        else
        {
            taxAmountCur = correctedTaxAmountCur;
        }
    }

    correctedTaxAmountCur = taxAmountCur;

    return taxAmountCur;
}

display TaxAmountJournal taxAmountJournal(LedgerJournalTrans _ledgerJournalTrans)
{
    TaxAmountJournal taxAmountJournal;
    if (!element.shouldSkipTaxCalculation())
    {
        taxAmountJournal = custVendPrepaymentHandling.taxAmountJournal(_ledgerJournalTrans);
    }

    if (taxAmountJournal      == 0 &&
        correctedTaxAmountCur != 0)
    {
        correctedTaxAmountCur = 0;
        custVendPrepaymentHandling.parmLedgerJournalEngine().correctTaxAmount(_ledgerJournalTrans, 0);
    }

    return taxAmountJournal;
}

