public void modified()
{
    #ISOCountryRegionCodes
    boolean allowEdit;

    super();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
    {
        allowEdit = ledgerJournalTrans.CustVendAccount_PL? false : true;
        custVendPrepaymentHandling.parmLedgerJournalEngine().initFromCustVendVatRegNums_PL(ledgerJournalTrans);
        ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, VATNumJournal)).allowEdit(allowEdit);
        ledgerJournalTrans_ds.object(FieldNum(LedgerJournalTrans, Addressing_PL)).allowEdit(allowEdit);
        ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, CustVendName_PL)).allowEdit(allowEdit);
    }
}

