public void write()
{
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]) &&
        (!taxServiceTariff.ParentRecId || !taxServiceTariff.ParentTableId))
    {
        // Write can cause the child data source to lose its foreign key values so restore them.
        taxServiceTariff.ParentRecId = ledgerJournalTrans.RecId;
        taxServiceTariff.ParentTableId = ledgerJournalTrans.TableId;
        taxServiceTariff_ds.refresh();
    }

    super();
}

void clicked()
{
    // since ax6 FormDataSource is not created for tmp table (starting with ax6), but required by
    // TaxTmpWorkTrans form, fake datasource is created
    LedgerJournalTrans trans = custVendPrepaymentHandling.ledgerJournalTrans();
    ledgerJournalTrans.data(custVendPrepaymentHandling.ledgerJournalTrans());
    Tax::showTaxes(menuitemDisplayStr(TaxTmpWorkPrepayment_RU), custVendPrepaymentHandling.getTax(), ledgerJournalTrans, element);
}

public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        ret = custVendPrepaymentHandling.checkTransDate(this.dateValue());
        if (ret)
        {
            ledgerJournalTrans_ds.research();
        }
    }

    if (ret)
    {
        custVendPrepaymentHandling.parmTransDate(this.dateValue());
    }
    else
    {
        this.dateValue(custVendPrepaymentHandling.parmTransDate());
    }

    return ret;
}

public void clicked()
{
    boolean convert = this.checked();

    super();

    custVendPrepaymentHandling.parmLedgerJournalEngine().clearTaxAmountCache(ledgerJournalTrans);

    custVendPrepaymentHandling.parmConversion(convert);

    ledgerJournalTrans.setTmpData(custVendPrepaymentHandling.tmpLedgerJournalTrans());
    ledgerJournalTrans_ds.research();

    ledgerJournalTrans_ds.allowEdit   (! convert);
    ledgerJournalTrans_ds.allowDelete (! convert);
    ledgerJournalTrans_ds.allowCreate (! convert);
    custAdvanceInvoiceProposal.enabled(! convert);
}
