Method.Source:
    [Form]
    public class CustVendTransPostingLog_RU extends FormRun
    {
        CustVendTrans   custVendTrans;

        QueryBuildRange transRecIdRange, refTableIdRange;
        QueryBuildRange criteriaOpen;
        FormRun         ledgerTransVoucher;
        FormRun         taxTrans;

}

private void addLedgerTransVoucherRanges(FormRun _ledgerTransVoucher)
{
    QueryBuildDataSource queryBuildDataSource;
    FormDataSource       formDataSource;
    QueryBuildRange      queryBuildRange;

    formDataSource = _ledgerTransVoucher.dataSource();

    queryBuildDataSource = formDataSource.query().dataSourceTable(tableNum(SubledgerVoucherGeneralJournalEntry));
    if (queryBuildDataSource == null)
    {
        // add the SubledgerVoucherGeneralJournalEntry as a datasource since it always contains the voucher
        queryBuildDataSource = formDataSource.query().dataSourceTable(tableNum(GeneralJournalEntry));
        queryBuildDataSource = SubledgerVoucherGeneralJournalEntry::addDataSource(queryBuildDataSource);
    }

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(SubledgerVoucherGeneralJournalEntry, Voucher));
    queryBuildRange.value(queryValue(custVendTransPostingLog_RU.Voucher));

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(SubledgerVoucherGeneralJournalEntry, AccountingDate));

    if (custVendTransPostingLog_RU.Reversed)
    {
        queryBuildRange.value(queryRangeConcat(queryValue(custVendTransPostingLog_RU.TransDate), custVendTransPostingLog_RU.reverseDate()));
    }
    else
    {
        queryBuildRange.value(queryValue(custVendTransPostingLog_RU.TransDate));
    }
}

public void init()
{
    super();

    if (this.args() && this.args().dataset())
    {
        custVendTrans = this.args().record();
        moduleCustVend.visible(false);
    }
}

void ledgerTransVoucherInit()
{
    Args                 args;

    args = new Args(formStr(LedgerTransVoucher));
    args.caller(this);
    args.parmEnum(AllChosen::Chosen);
    ledgerTransVoucher = classfactory.formRunClass(args);
    ledgerTransVoucher.init();

    this.addLedgerTransVoucherRanges(ledgerTransVoucher);
    ledgerTransVoucher.run();
    ledgerTransVoucher.wait();
}

void ledgerTransVoucherLinkActive()
{
    FormDataSource       formDataSource;

    if (ledgerTransVoucher && ! ledgerTransVoucher.closed())
    {
        this.addLedgerTransVoucherRanges(ledgerTransVoucher);
        formDataSource.executeQuery();
    }
}

void taxTransInit()
{
    Args                 args;
    QueryBuildDataSource qBd, qBdTaxTrans_RU;
    FormDataSource       formDataSource;
    QueryBuildRange      qBr, qBRTaxTrans_RU;

    args = new Args(formStr(TaxTrans));
    args.caller(this);
    args.record(custVendTransPostingLog_RU);
    taxTrans = classfactory.formRunClass(args);
    taxTrans.init();
    formDataSource = taxTrans.dataSource();
    qBd = formDataSource.query().dataSourceTable(tableNum(TaxTrans));
    qBr = qBd.addRange(fieldNum(TaxTrans,Voucher));
    qBr.value(queryValue(custVendTransPostingLog_RU.Voucher));

    qBdTaxTrans_RU = TaxTrans::addDataSource(qBd, tableNum(TaxTrans_RU));
    qBRTaxTrans_RU = qBdTaxTrans_RU.addRange(fieldNum(TaxTrans_RU, CustVendTransPostingLog_RU));
    qBRTaxTrans_RU.value(queryValue(custVendTransPostingLog_RU.RecId));

    qBr = qBd.addRange(fieldNum(TaxTrans,TransDate));
    if (custVendTransPostingLog_RU.Reversed)
    {
        qBr.value(queryRangeConcat(queryValue(custVendTransPostingLog_RU.TransDate),custVendTransPostingLog_RU.reverseDate()));
    }
    else
    {
         qBr.value(queryValue(custVendTransPostingLog_RU.TransDate));
    }
    taxTrans.run();
    taxTrans.wait();
}

void taxTransLinkActive()
{
    QueryBuildDataSource qBd, qBdTaxTrans_RU;
    FormDataSource       formDataSource;
    QueryBuildRange      qBr, qBrTaxTrans_RU;
    if (taxTrans && ! taxTrans.closed())
    {
        formDataSource = taxTrans.dataSource();
        qBd = formDataSource.query().dataSourceTable(tableNum(TaxTrans));
        qBd.clearRanges();
        qBr = qBd.addRange(fieldNum(TaxTrans, Voucher));
        qBr.value(queryValue(custVendTransPostingLog_RU.Voucher));

        qBdTaxTrans_RU = taxTrans::addDataSource(qBd, tableNum(TaxTrans_RU));
        qBrTaxTrans_RU = qBdTaxTrans_RU.addRange(fieldNum(TaxTrans_RU, CustVendTransPostingLog_RU));
        qBrTaxTrans_RU.value(queryValue(custVendTransPostingLog_RU.RecId));

        qBr = qBd.addRange(fieldNum(TaxTrans,TransDate));
        if (custVendTransPostingLog_RU.Reversed)
        {
            qBr.value(queryRangeConcat(queryValue(custVendTransPostingLog_RU.TransDate),custVendTransPostingLog_RU.reverseDate()));
        }
        else
        {
            qBr.value(queryValue(custVendTransPostingLog_RU.TransDate));
        }
        formDataSource.executeQuery();
    }
}

public void executeQuery()
{
    switch (showOpen.value())
    {
        case 1 :
            criteriaOpen.value(queryValue(NoYes::No));
            break;

        case 0 :
            criteriaOpen.value(SysQuery::valueUnlimited());
            break;
    }
    super();
}

public int active()
{
    int ret = super();

    if (custVendTransPostingLog_RU.moduleCustVend() == ModuleCustVend::Cust)
    {
        custTrans.visible(true);
        offsetCustTrans.visible(true);
        vendTrans.visible(false);
        offsetVendTrans.visible(false);
    }
    else
    {
        custTrans.visible(false);
        offsetCustTrans.visible(false);
        vendTrans.visible(true);
        offsetVendTrans.visible(true);
    }

    element.ledgerTransVoucherLinkActive();
    element.taxTransLinkActive();

    return ret;
}

public void linkActive()
{
    transRecIdRange.value(strFmt('(' +
                          new DictField(tableNum(CustVendTransPostingLog_RU),
                                        fieldNum(CustVendTransPostingLog_RU, TransRecId)).name() +
                          ' == %1' + ')' + ' || ' +
                          '(' +
                          new DictField(tableNum(CustVendTransPostingLog_RU),
                                        fieldNum(CustVendTransPostingLog_RU, OffSetRecId)).name() +
                          ' == %1' + ')',
                          custVendTrans.RecId));

    refTableIdRange.value(queryValue(custVendTrans.TableId));

    super();
}

public void init()
{
    super();

    transRecIdRange = this.query().dataSourceTable(tableNum(CustVendTransPostingLog_RU)).addRange(fieldNum(CustVendTransPostingLog_RU, TransRecId));
    transRecIdRange.status(RangeStatus::Hidden);

    refTableIdRange = this.query().dataSourceTable(tableNum(CustVendTransPostingLog_RU)).addRange(fieldNum(CustVendTransPostingLog_RU, RefTableId));

    this.cacheAddMethod(tableMethodStr(CustVendTransPostingLog_RU, reverseDate));
    criteriaOpen = this.query().dataSourceNo(1).addRange(fieldNum(CustVendTransPostingLog_RU,Reversed));

    this.query().dataSourceTable(tableNum(CustVendTransPostingLog_RU)).clearDynalinks();
}

void clicked()
{
    ;
    if (! ledgerTransVoucher || ledgerTransVoucher.closed())
    {
        element.ledgerTransVoucherInit();
    }
}

void clicked()
