void clicked()
{
    Args        args = new Args();
    DataAreaId  companyId;

    if (xDataArea::exist(custVendTransPostingLog_RU.OffsetCompany))
    {
        companyId = custVendTransPostingLog_RU.OffsetCompany;
    }
    else
    {
        companyId = curext();
    }

    changecompany(companyId)
