public void init()
{
    FormDataObject  fdo;

    super();

    fromTransportPointTypeFilter.selection(CustVendTransportPointType::Warehouse);

    if (element.args() && element.args().record())
    {
        switch (element.args().record().TableId)
        {
            case tableNum(InventLocation) :
                toTransportPointTypeFilter.allowEdit(true);
                toTransportPointTypeFilter.skip(false);
                fdo = transportPointLine_DS.object(fieldNum(CustVendTransportPointLine,FromInventLocation));
                fdo.visible(false);
                fdo.skip(true);
                break;

            default :
                toTransportPointTypeFilter.visible(false);
                toTransportPointTypeFilter.skip(true);
                break;
        }
        callerTable = element.args().record();
        if (!isConfigurationkeyEnabled(configurationKeyNum(InventTransferOrders)))
        {
            toTransportPointTypeFilter.selection(CustVendTransportPointType::Address);
        }
    }
    else
    {
        toTransportPointTypeFilter.visible(true);
        toTransportPointTypeFilter.allowEdit(true);
        toTransportPointTypeFilter.skip(false);
        toTransportPointTypeFilter.selection(CustVendTransportPointType::Address);
    }

    element.updateFilterCaller();
}

void setRange()
{
    toCountryRange.value(toCountryRegionIdFilter.text());
    toStateRange.value(toStateFilter.text());
    toCountyRange.value(toCountyFilter.text());
    toZipCodeRange.value(toZipCodeFilter.text());
    toWarehouseRange.value(toInventLocationFilter.text());
    toTransportPointTypeRange.value(queryValue(toTransportPointTypeFilter.selection()));
    fromWarehouseRange.value(fromInventLocationFilter.text());
}

