void updateFilters()
{
    QueryBuildDataSource    qbds = transportPointLine_DS.queryBuildDataSource();
    QueryBuildRange         qbr;

    qbr = qbds.findRange(fieldNum(CustVendTransportPointLine, FromInventLocation));
    if (qbr)
    {
        fromWarehouseSelection = qbr.value();
        fromInventlocationFilter.text(fromWarehouseSelection);
    }
    qbr = qbds.findRange(fieldNum(CustVendTransportPointLine, ToInventLocation));
    if (qbr)
    {
        toWarehouseSelection = qbr.value();
        toInventlocationFilter.text(toWarehouseSelection);
    }
    qbr = qbds.findRange(fieldNum(CustVendTransportPointLine, ToCountryRegionId));
    if (qbr)
    {
        toCountrySelection = qbr.value();
        toCountryRegionIdFilter.text(toCountrySelection);
    }
    qbr = qbds.findRange(fieldNum(CustVendTransportPointLine, ToState));
    if (qbr)
    {
        toStateSelection = qbr.value();
        toStateFilter.text(toStateSelection);
    }
    qbr = qbds.findRange(fieldNum(CustVendTransportPointLine, ToCounty));
    if (qbr)
    {
        toCountySelection = qbr.value();
        toCountyFilter.text(toCountySelection);
    }
    qbr = qbds.findRange(fieldNum(CustVendTransportPointLine, ToZipCode));
    if (qbr)
    {
        toZipCodeSelection = qbr.value();
        toZipCodeFilter.text(toZipCodeSelection);
    }

    this.validateFilters(); // Some of the entered values might not be valid, so validation is needed
}

