public void linkActive()
{
    LogisticsAddressCountryRegion    countryRegionCaller;
    LogisticsAddressState            stateCaller;
    LogisticsAddressCounty           countyCaller;
    LogisticsAddressZipCode          zipCodeCaller;
    InventLocation                   inventLocationCaller;

    switch (element.args().dataset())
    {
        case tableNum(LogisticsAddressCountryRegion) :
            countryRegionCaller = element.args().record();
            toCountryRegionIdFilter.text(countryRegionCaller.CountryRegionId);
            break;

        case tableNum(LogisticsAddressState)   :
            stateCaller = element.args().record();
            toCountryRegionIdFilter.text(stateCaller.CountryRegionId);
            toStateFilter.text(stateCaller.StateId);
            break;

        case tableNum(LogisticsAddressCounty)  :
            countyCaller = element.args().record();
            toCountryRegionIdFilter.text(countyCaller.CountryRegionId);
            toStateFilter.text(countyCaller.StateId);
            toCountyFilter.text(countyCaller.CountyId);
            break;

        case tableNum(LogisticsAddressZipCode) :
            zipCodeCaller = element.args().record();
            toCountryRegionIdFilter.text(zipCodeCaller.CountryRegionId);
            toStateFilter.text(zipCodeCaller.State);
            toCountyFilter.text(zipCodeCaller.County);
            toZipCodeFilter.text(zipCodeCaller.ZipCode);
            break;

        case tableNum(InventLocation) :
            inventLocationCaller = element.args().record();
            fromInventLocationFilter.text(inventLocationCaller.InventLocationId);
            break;
    }

    element.setRange();

    super();
}

public void init()
{
    super();

    fromWarehouseRange        = transportPointLine_ds.query().dataSourceTable(tableNum(CustVendTransportPointLine)).addRange(fieldNum(CustVendTransportPointLine,FromInventLocation));
    toWarehouseRange          = transportPointLine_ds.query().dataSourceTable(tableNum(CustVendTransportPointLine)).addRange(fieldNum(CustVendTransportPointLine,ToInventLocation));
    toCountryRange            = transportPointLine_ds.query().dataSourceTable(tableNum(CustVendTransportPointLine)).addRange(fieldNum(CustVendTransportPointLine,ToCountryRegionId));
    toStateRange              = transportPointLine_ds.query().dataSourceTable(tableNum(CustVendTransportPointLine)).addRange(fieldNum(CustVendTransportPointLine,ToState));
    toCountyRange             = transportPointLine_ds.query().dataSourceTable(tableNum(CustVendTransportPointLine)).addRange(fieldNum(CustVendTransportPointLine,ToCounty));
    toZipCodeRange            = transportPointLine_ds.query().dataSourceTable(tableNum(CustVendTransportPointLine)).addRange(fieldNum(CustVendTransportPointLine,ToZipCode));
    toTransportPointTypeRange = transportPointLine_ds.query().dataSourceTable(tableNum(CustVendTransportPointLine)).addRange(fieldNum(CustVendTransportPointLine,ToTransportPointType));
}

/// <summary>
/// Updates the default transport days value of the main transport relation.
/// </summary>
public void updateTransportDaysForTheTransportRelation(boolean _isDefaultDlvModeEnabled, CustVendTransportDays _transportDays)
{
    if ( _isDefaultDlvModeEnabled)
    {
        transportPointLine.TransportDays = _transportDays;
    }
}

/// <summary>
///  Returns true, if default delivery mode is defined for the given relation. Otherwise, false.
/// </summary>
public boolean hasDefaulDeliveryMode()
{
    return CustVendTransportTime::defaultTransportTime(transportPointLine.RecId) != null ;
}

public void create(boolean _append = false)
{
    if (transportPointLine_ds.cursor().RecId)
    {
        super(_append);
    }
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        // Default delivery mode field should be editable if current record already contains the default delivery mode
        // or there is no default delivery mode specifield for the given main relation.
        CustVendTransportTime custVendTransportTime = CustVendTransportTime::defaultTransportTime(TransportPointLine_DS.cursor().RecId);
        boolean isDefaultDlvModeEditable = (transportTime.Recid == custVendTransportTime.RecId) || (custVendTransportTime.RecId == 0);

        FormDataObject fdo = TransportTime_DS.object(fieldnum(CustVendTransportTime, DefaultDlvMode));
        fdo.allowEdit(isDefaultDlvModeEditable);
    }
    return ret;
}

