}

/// <summary>
/// Transport days of the main transport relation should be updated when this field does.
/// </summary>
/// <returns></returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    CustVendTransportDays defaultTransportDays = TransportTime_TransportDays.value();
    transportPointLine_ds.updateTransportDaysForTheTransportRelation(TransportTime_DefaultDlvMode.value(), defaultTransportDays);

    return ret;
}

/// <summary>
///  If a default delivery mode is set, then the transport times to be used for a specific transport relation
///  should not be editable and the default transport days value should be copied into the main transport relation.
/// </summary>
/// <returns></returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    boolean isDefaultDlvModeEnabled = TransportTime_DefaultDlvMode.value();
    transportPointLine_ds.updateTransportDaysForTheTransportRelation(isDefaultDlvModeEnabled, TransportTime_TransportDays.value());
    TransportDays_TransportDays.enabled(!isDefaultDlvModeEnabled);
    TransportPointLine_TransportDays.enabled(!isDefaultDlvModeEnabled);

    return ret;
}

