public boolean validate()
{
    boolean ret;

    ret = super();

    if (this.text())
    {
        if (!LogisticsAddressState::exist(toCountryRegionIdFilter.text(), toStateFilter.text()))
        {
            return checkFailed(strFmt("@SYS112224",
                            toStateFilter.text(), toStateFilter.labelText(), tablePName(LogisticsAddressState)));
        }
    }

    return ret;
}

