public void lookup()
{
    CustVendTransportPointLine  addressBuf;

    addressBuf.setTmp();
    addressBuf.ToState              = toStateFilter.text();
    addressBuf.ToCounty             = toCountyFilter.text();
    addressBuf.ToCountryRegionId    = toCountryRegionIdFilter.text();
    addressBuf.insert();

    LogisticsAddressZipCode::zipCodeLookup(this,addressBuf);
}

public boolean modified()
{
    boolean                     ret;
    CustVendTransportPointLine  addressBuf;

    ret = super();

    if (ret)
    {
        addressBuf.ToZipCode            = toZipCodeFilter.text();
        addressBuf.ToCountryRegionId    = toCountryRegionIdFilter.text();
        addressBuf.ToCounty             = toCountyFilter.text();
        addressBuf.ToState              = toStateFilter.text();
        addressBuf.LogisticsPostalAddressMap::modifiedFieldZipCode();

        toCountryRegionIdFilter.text(addressBuf.ToCountryRegionId);
        toStateFilter.text(addressBuf.ToState);
        toCountyFilter.text(addressBuf.ToCounty);

        element.updateFilterSelection();
    }

    return ret;
}

