Method.Source:
    [Form]
    public class CustomCorrectionTable_RU extends FormRun
    {
        FactureSource_RU                    factureSource;
        CustomCorrectionTotalAmounts_RU     customCorrectionTotalAmounts;
        CustomJournalTransOnDateAmounts_RU  customJournalTransOnDateAmounts;

        SysQueryRun                         sysQueryRun;
        TmpCustomJournalTotals_RU           tmpCustomJournalTotals;

        private const int currentVersion = 1;

}

void calcTotals()
{
    CustomCorrectionTotalAmounts_RU  totalAmounts;
    TmpCustomJournalTotalsFilters_RU totalsFilters;
    QueryBuildDataSource             qbds = sysQueryRun.query().dataSourceNo(1);

    CustomTotalFilterType_RU filterType(fieldId _fieldId)
    {
        CustomTotalFilterType_RU ret;

        switch (_fieldId)
        {
            case fieldnum(CustomCorrectionTrans_RU, ItemId)  :
                ret = CustomTotalFilterType_RU::Item;
                break;
            case fieldnum(CustomCorrectionTrans_RU, feaccId) :
                ret = CustomTotalFilterType_RU::FEACC;
                break;
            case fieldnum(CustomCorrectionTrans_RU, CustomPartition) :
                ret = CustomTotalFilterType_RU::CustomPartition;
                break;
            case fieldnum(CustomCorrectionTrans_RU, CountryRegionId) :
                ret = CustomTotalFilterType_RU::CountryRegionId;
        }

        return ret;
    }

    void setFilter(fieldId _fieldId)
    {
        QueryBuildRange qbr = qbds.findRange(_fieldId);

        if (qbr)
        {
            totalsFilters.initRange(customCorrectionTable.CorrectionId, filterType(_fieldId), qbr.value());
        }
    }

    setFilter(fieldnum(CustomCorrectionTrans_RU, ItemId));
    setFilter(fieldnum(CustomCorrectionTrans_RU, feaccId));
    setFilter(fieldnum(CustomCorrectionTrans_RU, CustomPartition));
    setFilter(fieldnum(CustomCorrectionTrans_RU, CountryRegionId));

    totalAmounts = CustomCorrectionTotalAmounts_RU::createFromCorrection(customCorrectionTable, totalsFilters);
    tmpCustomJournalTotals.initFromCustomCorrectionTable(customCorrectionTable, totalAmounts, !totalAmounts.useFilter());
}

void close()
{
    xSysLastValue::saveLast(this);

    super();
}

public CustomJournalTransOnDateAmounts_RU customJournalTransOnDateAmounts(boolean _renew = false)
{
    if (_renew || ! customJournalTransOnDateAmounts)
    {
        customJournalTransOnDateAmounts = CustomJournalTransOnDateAmounts_RU::createFromTransOnDate(customCorrectionTrans.customJournalTrans(), customCorrectionTable.CorrectionDate);
    }

    return customJournalTransOnDateAmounts;
}

public void enableControls(boolean _updateButtons = false)
{
    boolean                 enabled;
    boolean                 factured;
    CustomJournalTable_RU   customJournalTableLoc = customCorrectionTable.customJournalTable();

    enabled = (customJournalTableLoc.CounteragentType == CustomPartnerType_RU::Custom) ? false : true;
    customCorrectionTable_ds.object(fieldnum(CustomCorrectionTable_RU, CounteragentType)).allowEdit(enabled && customCorrectionTable.CorrectionStatus != CustomCorrectionStatus_RU::Posted);

    enabled = (customJournalTableLoc.CustomJournalType == CustomJournalType_RU::Export) ? false : true;
    customCorrectionTable_ds.object(fieldnum(CustomCorrectionTable_RU, CostCorrection)).allowEdit(enabled && customCorrectionTable.CorrectionStatus != CustomCorrectionStatus_RU::Posted);

    customCorrectionTable_ds.object(fieldnum(CustomCorrectionTable_RU, CorrectionJournalId)).allowEdit(false);
    customCorrectionTable_ds.object(fieldnum(CustomCorrectionTable_RU, JournalId)).allowEdit(false);
    customCorrectionTable_ds.object(fieldnum(CustomCorrectionTable_RU, FactureId)).allowEdit(false);
    customCorrectionTable_ds.object(fieldnum(CustomCorrectionTable_RU, CorrectionStatus)).allowEdit(false);
    customCorrectionTable_ds.object(fieldnum(CustomCorrectionTable_RU, CorrectionReason)).allowEdit(customCorrectionTable.CorrectionStatus != CustomCorrectionStatus_RU::Posted);
    customCorrectionTable_ds.object(fieldnum(CustomCorrectionTable_RU, Storno)).allowEdit(customCorrectionTable.CorrectionStatus != CustomCorrectionStatus_RU::Posted);
    customCorrectionTable_ds.object(fieldnum(CustomCorrectionTable_RU, Description)).allowEdit(customCorrectionTable.CorrectionStatus != CustomCorrectionStatus_RU::Posted);

    customCorrectionTable_ds.allowDelete(customCorrectionTable.CorrectionStatus == CustomCorrectionStatus_RU::Project);
    customCorrectionTrans_ds.allowEdit(customCorrectionTable.CorrectionStatus != CustomCorrectionStatus_RU::Posted);
    customCorrectionTrans_ds.allowDelete(customCorrectionTable.CorrectionStatus != CustomCorrectionStatus_RU::Posted);

    if (_updateButtons)
    {
        factured = (FactureJour_RU::find(customCorrectionTable.FactureId, FactureModule_RU::Vend).RecId != 0);
        customJournalCorrectionFactureCreate_RU.enabled(!factured && customCorrectionTable.CorrectionStatus == CustomCorrectionStatus_RU::Posted);
        factureJournalVend.enabled(factured);
        customJournalCorrectionPost.enabled(customCorrectionTable.CorrectionStatus != CustomCorrectionStatus_RU::Posted);
        ledgerTransVoucher.enabled(customCorrectionTable.CorrectionStatus == CustomCorrectionStatus_RU::Posted);
    }
}

public void init()
{
    this.initFactureSource_RU();

    if (! element.args().record() ||
        (element.args().dataset() != tablenum(CustomJournalTable_RU) && element.args().dataset() != tablenum(CustomCorrectionTable_RU)) && !factureSource)
        throw error(strfmt("@SYS19096", formstr(CustomCorrectionTable_RU), tableid2pname(tablenum(CustomJournalTable_RU))));

    super();

    xSysLastValue::getLast(this);
    element.setTitleText();
}

void initFactureSource_RU()
{
    if (element.args().caller() &&
        SysDictClass::isEqualOrSuperclass(classidget(element.args().caller()), classnum(FactureSource_RU)))
    {
        factureSource = element.args().caller();
    }
}

public void initParmDefault()
{
    element.initQuery();
}

void initQuery()
{
    Query                query;
    QueryBuildDataSource qbds;
    QueryBuildRange      qbr;

    query = new Query();
    qbds  = query.addDataSource(tablenum(CustomCorrectionTrans_RU));
    qbr   = qbds.addRange(fieldnum(CustomCorrectionTrans_RU, CorrectionJournalId));

    qbr.value(customCorrectionTable.CorrectionJournalId);
    qbr.status(RangeStatus::Locked);

    qbds.addRange(fieldnum(CustomCorrectionTrans_RU, ItemId));
    qbds.addRange(fieldnum(CustomCorrectionTrans_RU, feaccId));
    qbds.addRange(fieldnum(CustomCorrectionTrans_RU, CustomPartition));
    qbds.addRange(fieldnum(CustomCorrectionTrans_RU, CountryRegionId));

    sysQueryRun  = new SysQueryRun(query);
    sysQueryRun.promptLoadLastUsedQuery(false);
    sysQueryRun.promptAllowSave(false);
    sysQueryRun.promptShowSorting(false);
    sysQueryRun.promptSaveQueryPrUser(false);
    sysQueryRun.promptAllowAddDataSource(false);
    sysQueryRun.promptAllowAddRange(QueryAllowAdd::NoFields);
}

