public void setTitleText()
{
    if (customCorrectionTable.CorrectionId)
    {
        headerTitle.text(strFmt('%1 : %2 - %3', customCorrectionTable.CorrectionId,
                                                customCorrectionTable.customJournalTable().PartnerCode,
                                                customCorrectionTable.customJournalTable().partnerName()));
    }
    else
    {
        headerTitle.text("@GLS115146");
    }
}

public FormDataSource tableFormDataSource()
{
    return customCorrectionTable_ds;
}

public FormDataSource transFormDataSource()
{
    return customCorrectionTrans_ds;
}

public boolean unpack(container packedClass)
{
    Version     version = RunBase::getVersion(packedClass);
    container   queryCon;

    switch (version)
    {
        case currentVersion:
            [version, queryCon] = packedClass;
            if (SysQuery::isPackedOk(queryCon))
                sysQueryRun = new SysQueryRun(queryCon);
            else
                this.initQuery();
            break;
        default:
            return false;
    }

    return true;
}

public void create(boolean _append = false)
{
    Args args = new Args();

    args.caller(element);
    args.record(customCorrectionTable);

    new MenuFunction(menuitemactionstr(CustomCorrectionCreate_RU), MenuItemType::Action).run(args);
}

