public int active()
{
    boolean enabled = (customCorrectionTable.customJournalTable().CustomJournalType == CustomJournalType_RU::Export) ? false : true;
    int ret;

    ret = super();

    element.customJournalTransOnDateAmounts(true);
    newLine.enabled(customCorrectionTrans.customCorrectionTable().CorrectionStatus != CustomCorrectionStatus_RU::Posted);

    customCorrectionTrans_ds.object(fieldnum(CustomCorrectionTrans_RU, CorrectionExcise)).allowEdit(enabled);
    customCorrectionTrans_ds.object(fieldnum(CustomCorrectionTrans_RU, CorrectionVAT)).allowEdit(enabled);

    return ret;
}

