void clicked()
{
    FormRun              formRun;
    Args                 args;

    args = new Args(formstr(SysQueryForm));
    args.caller(sysQueryRun);

    formRun = ClassFactory::formRunClassOnClient(args);
    formRun.init();
    formRun.run();
    formRun.wait();

    if (formRun.closedOk())
    {
        element.calcTotals();

        args = new Args(formstr(CustomJournalTotals_RU));
        args.record(tmpCustomJournalTotals);
        args.caller(sysQueryRun);

        formRun = ClassFactory::formRunClassOnClient(args);
        formRun.init();
        formRun.run();
        formRun.detach();
    }
}

public void clicked()
{
    super();

    element.enableControls(true);
}
