[Form]
    public class CustomPartitionAmounts_RU extends FormRun
    {
        CustomJournalTable_RU               customJournalTable;
        CustomJournalTotalAmounts_RU        customJournalTotalAmounts;
        [FormObservable]
        CurrencyCode                        customCurrencyCode;
        [FormObservable]
        CurrencyCode                        customStatisticCurrencyCode;
        TmpCustomJournalTotalsFilters_RU    tmpCustomJournalTotalsFilters;
        [FormObservable]
        DlvTermId                           dlvTermCode;

        CustomJournalTrans_RU               customJournalTrans_Caller;
}

public void customJournalTotalAmountsCalc()
{
    if (customJournalTable.RecId)
    {
        tmpCustomJournalTotals.initFromCustomJournalTable(customJournalTable, customJournalTotalAmounts);
        tmpCustomJournalTotals_ds.executeQuery();
    }
}

edit CustomStatisticCurrencyCode customStatisticCurrencyCode(boolean _set, CustomStatisticCurrencyCode _currencyCode = customStatisticCurrencyCode)
{
    if (_set)
    {
        customStatisticCurrencyCode = _currencyCode;
        element.reCalcAmounts();
    }
    return customStatisticCurrencyCode;
}

public void init()
{
    if (element.args().dataset() != tablenum(CustomJournalTrans_RU))
    {
        throw error("@SYS22539");
    }

    customJournalTrans_Caller = element.args().record();

    super();

    customCurrencyCode          = CompanyInfoHelper::standardCurrency();
    customStatisticCurrencyCode = InventParameters::find().CustomStatisticCurrencyCode_RU;

    if (! customStatisticCurrencyCode)
    {
        checkFailed("@GLS115408");
        customStatisticCurrencyCode = customCurrencyCode;
    }
}

void reCalcAmounts()
{
    customJournalTable = customJournalTrans.customJournalTable();
    dlvTermCode        = customJournalTable.DlvTermId;
    if (!dlvTermCode)
    {
        dlvTermCode    = AgreementHeaderDefault::findAgreementHeader(
            customJournalTable.agreementHeaderExt().AgreementHeader).DeliveryTerm;
    }

    delete_from tmpCustomJournalTotalsFilters;

    tmpCustomJournalTotalsFilters.initRange(customJournalTrans.CustomJournalId, CustomTotalFilterType_RU::CustomPartition, customJournalTrans.CustomPartition);
    customJournalTotalAmounts = CustomJournalTotalAmounts_RU::createFromJournal(customJournalTrans.customJournalTable(), tmpCustomJournalTotalsFilters);

    element.customJournalTotalAmountsCalc();
}

    void reCreateMarkupTrans()
    {
        VendInvoiceTrans            vendInvoiceTrans;
        MarkupTrans                 markupTransSource;
        MarkupDlvTermConnection_RU  markupDlvTermConnection;
        Common                      common;
        UnitOfMeasureSymbol         fromUnit;
        Qty                         qty;
        boolean                     fractionQty;

        delete_from markupTrans;

        if (customJournalTable.CustomJournalType == CustomJournalType_RU::Export)
        {
            return;
        }

        switch (customJournalTable.CustomInventTransStatus)
        {
            case CustomInventTransStatus_RU::Purchased  :
                select vendInvoiceTrans
                       index hint TransIdIdx
                       where vendInvoiceTrans.InventTransId == customJournalTrans.SalesPurchInventTransId &&
                             vendInvoiceTrans.InvoiceId     == customJournalTrans.InvoiceId &&
                             vendInvoiceTrans.InvoiceDate   == customJournalTrans.InvoiceDate;
                common   = vendInvoiceTrans;
                qty      = common.(fieldnum(VendInvoiceTrans, Qty));
                fromUnit = common.(fieldNum(VendInvoiceTrans, PurchUnit));
                break;
            case CustomInventTransStatus_RU::Ordered,
                CustomInventTransStatus_RU::Empty      :
                common   = customJournalTrans.salesPurchLine();
                qty      = common.(fieldnum(PurchLine, PurchQty));
                fromUnit = common.(fieldNum(PurchLine, PurchUnit));
                break;
        }

        if (fromUnit != customJournalTrans.UnitId)
        {
            EcoResProductUnitConverter ecoResProductUnitConverter = EcoResProductUnitConverter::newGivenUnitSymbolsForReleasedProduct(customJournalTrans.ItemId,
																																	  customJournalTrans.InventDimId,
																																	  fromUnit,
																																	  customJournalTrans.UnitId,
																																	  NoYes::No,
																																	  NoYes::No);
            if (!ecoResProductUnitConverter.canValueBeConverted())
            {
                throw error(strFmt("@SYS50546", fromUnit, customJournalTrans.UnitId));
            }

                qty = ecoResProductUnitConverter.convertValue(qty);
        }

        if (qty)
        {
            fractionQty = customJournalTrans.Qty != decRound(qty, UnitOfMeasure::unitOfMeasureDecimalPrecision(UnitOfMeasure::unitOfMeasureIdBySymbol(customJournalTrans.UnitId)));
            while select markupTransSource
                where markupTransSource.TransTableId == common.TableId &&
                      markupTransSource.TransRecId   == common.RecId
                join recId from markupDlvTermConnection
                    where markupDlvTermConnection.MarkupCode  == markupTransSource.MarkupCode &&
                          markupDlvTermConnection.DlvTermCode == dlvTermCode
            {
                markupTrans.data(markupTransSource);
                if (fractionQty)
                {
                    markupTrans.Value = markupTransSource.Value * customJournalTrans.Qty / qty;
                }
                else
                {
                    markupTrans.Value = markupTransSource.Value;
                }
                markupTrans.Value = CurrencyExchangeHelper::amount(markupTrans.Value, customCurrencyCode);
                markupTrans.insert();
            }
        }

        markupTrans_DS.executeQuery();
    }

public void init()
{
    QueryBuildDataSource    queryBuildDataSource;

    super();

    queryBuildDataSource = this.query().dataSourceTable(tablenum(CustomJournalTrans_RU));
    queryBuildDataSource.clearDynalinks();

    queryBuildDataSource.addDynalink(fieldnum(CustomJournalTrans_RU, CustomJournalId), customJournalTrans_Caller, fieldnum(CustomJournalTrans_RU, CustomJournalId));
    queryBuildDataSource.addDynalink(fieldnum(CustomJournalTrans_RU, CustomPartition), customJournalTrans_Caller, fieldnum(CustomJournalTrans_RU, CustomPartition));
}

public int active()
{
    int ret;

    element.reCalcAmounts();
    element.reCreateMarkupTrans();

    ret = super();

    return ret;
}

public void displayOption(Common _record, FormRowDisplayOption _options)
{
    TmpCustomJournalTotals_RU record = _record;
    if (record.CustomTotalType == CustomTotalType_RU::Total)
    {
        _options.backColor(WinAPI::rgb2int(128, 255, 128));
    }

    super(_record, _options);
}

display CustomCorrectAmount_RU correctAmountCur(TmpCustomJournalTotals_RU _tmpCustomJournalTotals)
{
    return CurrencyExchangeHelper::curAmount2CurAmount(_tmpCustomJournalTotals.AmountCorrect, customCurrencyCode, customStatisticCurrencyCode, customJournalTable.PlanArrivalDate);
}

display CustomAmount_RU estimativeAmountCur(TmpCustomJournalTotals_RU _tmpCustomJournalTotals)
{
    return CurrencyExchangeHelper::curAmount2CurAmount(_tmpCustomJournalTotals.AmountEstimative, customCurrencyCode, customStatisticCurrencyCode, customJournalTable.PlanArrivalDate);
}

display TotalAmount_RU totalAmountCur(TmpCustomJournalTotals_RU _tmpCustomJournalTotals)
{
    return CurrencyExchangeHelper::curAmount2CurAmount(_tmpCustomJournalTotals.AmountEstimative + _tmpCustomJournalTotals.AmountCorrect, customCurrencyCode, customStatisticCurrencyCode, customJournalTable.PlanArrivalDate);
}
