void callerRefresh()
{
    FormRun  formObject;

    if (element.args().caller() && element.args().caller().name() == formstr(PurchTable))
    {
        formObject = element.args().caller();
        formObject.reload();
    }
}

public boolean canClose()
{
    boolean ret;

    ret = super();
    customsInvoiceRegnJournalTable_IN_ds.research();
    if (customsInvoiceRegnJournalTable_IN && !customsInvoiceRegnJournalTable_IN.validateWrite())
    {
        ret = false;
    }

    return ret;
}

public void close()
{
    if (purchTable_ds)
    {
        purchTable_ds.reread();
        purchTable_ds.refresh();
        purchTable_ds.active();
    }
    super();
}

public void displayInvoiceRegistrationJournal()
{
    QueryBuildDataSource                qbds;
    PurchTable                          purchTable;
    CustomsInvoiceRegnJournalTable_IN   customsInvoiceRegnJournalTable;
    CustomsInvoiceRegnJournalLine_IN    customsInvoiceRegnJournalLine;

    purchTable = purchTable_ds.cursor();
    qbds = customsInvoiceRegnJournalTable_IN_ds.query().dataSourceName(identifierstr(CustomsInvoiceRegnJournalTable_IN));
    qbds.addRange(fieldnum(CustomsInvoiceRegnJournalTable_IN, OrderId)).value(queryValue(purchTable.PurchId));

    // Because table and data Source buffer's  are different and to get latest record query on purch table.
    purchTable = PurchTable::find(purchTable.PurchId);
    select firstonly RecId from customsInvoiceRegnJournalTable
        where customsInvoiceRegnJournalTable.OrderId == purchTable.PurchId;

    customsInvoiceRegnJournalLine = CustomsInvoiceRegnJournalLine_IN::findRecId(customsInvoiceRegnJournalTable.RecId);
    if ((purchTable.DocumentStatus == DocumentStatus::BillOfEntry_IN
        && !CustomsVendBOETrans_IN::checkBillOfEntryComplete(purchTable.PurchId))
        || !CustomsInvoiceRegnTrans_IN::checkIfPurchQuantityConsumed(customsInvoiceRegnJournalLine))
    {
        element.insertInvoiceRegistrationJournal(purchTable_ds);
    }
}

/// <summary>
/// Retrieve the remaining import order line amounts from specific <c>PurchTable</c>
/// </summary>
/// <param name="_purchTable">
/// Specific <c>PurchTable</c>
/// </param>
public void fetchRemainingLines(PurchTable _purchTable)
{
    PurchLine                           localPurchLine;
    SysQueryRun                         chooselines;
    CustomsInvoiceRegnJournalLine_IN    customsInvoiceRegnJournalLine;
    CustomsInvoiceRegnJournalLine_IN    customsInvoiceRegnJournalLineLoc;

    if (!_purchTable)
    {
        return;
    }

    chooselines = new SysQueryRun(querystr(PurchUpdate));
    chooselines.query().dataSourceTable(tablenum(PurchTable)).addRange(fieldnum(PurchTable, PurchId)).value(queryValue(_purchTable.PurchId));

    ttsbegin;
    while (chooselines.next())
    {
        if (chooselines.changed(tablenum(PurchLine)))
        {
            localPurchLine                                                      = chooselines.get(tablenum(PurchLine));
            customsInvoiceRegnJournalLine.PurchLineRecId                        = localPurchLine.RecId;
            customsInvoiceRegnJournalLine.InventDimId                           = localPurchLine.InventDimId;
            customsInvoiceRegnJournalLine.CustomsInvoiceRegnJournalTableRecId   = CustomsInvoiceRegnJournalTable_IN.RecId;
            customsInvoiceRegnJournalLine.Receive = CustomsInvoiceRegistrateForm_IN::calcReceiveQty(localPurchLine);

            select sum(Receive) from customsInvoiceRegnJournalLineLoc
                where customsInvoiceRegnJournalLineLoc.PurchLineRecId == localPurchLine.RecId;

            if (customsInvoiceRegnJournalLine.Receive != 0)
            {
                if (customsInvoiceRegnJournalLine.Receive + customsInvoiceRegnJournalLineLoc.Receive == localPurchLine.PurchQty)
                {
                    customsInvoiceRegnJournalLine.CustomsInvoiceRegnJournalTableRecId = CustomsInvoiceRegnJournalTable_IN.RecId;
                    customsInvoiceRegnJournalLine.insert();
                }
                customsInvoiceRegnJournalLine_IN_ds.research();
            }
        }
    }
    ttscommit;
}

void init()
{
    CustomsInvoiceRegnJournalTable_IN   customsInvoiceRegnJournalTable;
    CustomsInvoiceRegnJournalLine_IN    customsInvoiceRegnJournalLine;
    PurchTable                          purchTable;
    #define.columnValue(8)

    super();

    element.updateDesign(InventDimFormDesignUpdate::Init);
    grid1.visibleColsValue(#columnValue + conlen(inventDimFormSetup.dimFieldsActive(inventDimFormSetup.parmDimParmVisibleGrid())));

    switch (element.args().dataset())
    {
        case tablenum(PurchTable)           :
            purchTable_ds = FormDataUtil::getFormDataSource(element.args().record());
            break;

        case tablenum(PurchLine)            :
            purchLine_ds = FormDataUtil::getFormDataSource(element.args().record());
            break;
        default                             :
            break;
    }

    purchTable = purchTable_ds.cursor();

    select firstonly RecId, PurchLineRecId from customsInvoiceRegnJournalLine
        join RecId from customsInvoiceRegnJournalTable
        where customsInvoiceRegnJournalLine.CustomsInvoiceRegnJournalTableRecId == customsInvoiceRegnJournalTable.RecId
            && customsInvoiceRegnJournalTable.OrderId == purchTable.PurchId;

    if (CustomsInvoiceRegnTrans_IN::checkInvoiceRegnComplete(customsInvoiceRegnJournalLine.PurchLineRecId))
    {
        element.displayInvoiceRegistrationJournal();
    }
    else
    {
        CustomsInvoiceRegnJournalLine_IN::clearLines();
        customsInvoiceRegnJournalTable_IN.clearTable();
        element.insertInvoiceRegistrationJournal(purchTable_ds);
    }

    customsInvoiceRegistrationForm = CustomsInvoiceRegistrateForm_IN::construct();
}

void insertInvoiceRegistrationJournal(FormDataSource _purchTable_ds)
{
    PurchTable                          localPurchTable;
    PurchLine                           localPurchLine;
    container                           purchRecordContainer;
    SysQueryRun                         chooselines;
    Map                                 mapPurchRecord;
    MapEnumerator                       mapPurchRecordEnumerator;
    CustomsInvoiceRegnJournalTable_IN   customsInvoiceRegnJournalTable;
    CustomsInvoiceRegnJournalLine_IN    customsInvoiceRegnJournalLine;
    RefRecId                            customsInvoiceRegnJournalTableRecId;
    CustomsInvoiceRegnJournalLine_IN    customsInvoiceRegnJournalLineLoc;

    chooselines = new SysQueryRun(querystr(PurchUpdate));
    chooselines.query().dataSourceTable(tablenum(PurchTable)).addRange(fieldnum(PurchTable, PurchId));

    purchRecordContainer = FormLetter::getFormRecord(_purchTable_ds);
    if (purchRecordContainer)
    {
        mapPurchRecord = Map::create(purchRecordContainer);
        mapPurchRecordEnumerator = mapPurchRecord.getEnumerator();

        while (mapPurchRecordEnumerator.moveNext())
        {
            localPurchTable = mapPurchRecordEnumerator.currentValue();
            chooselines.query().dataSourceTable(tablenum(PurchTable)).addRange(fieldnum(PurchTable, PurchId)).value(queryValue(localPurchTable.PurchId));
        }
    }

    ttsbegin;
    while (chooselines.next())
    {
        if (chooselines.changed(tablenum(PurchTable)))
        {
            localPurchTable                        = chooselines.get(tablenum(PurchTable));
            customsInvoiceRegnJournalTable.OrderId = localPurchTable.PurchId;
            customsInvoiceRegnJournalTableRecId    = CustomsInvoiceRegnJournalTable_IN::findByPurchId(localPurchTable.PurchId).RecId;

            if (!customsInvoiceRegnJournalTableRecId
               && PurchLine::checkReceiveQty_IN(localPurchTable.PurchId))
            {
                customsInvoiceRegnJournalTable.insert();
                customsInvoiceRegnJournalTableRecId = customsInvoiceRegnJournalTable.RecId;
            }
        }

        if (chooselines.changed(tablenum(PurchLine)))
        {
            localPurchLine                                                      = chooselines.get(tablenum(PurchLine));
            customsInvoiceRegnJournalLine.PurchLineRecId                        = localPurchLine.RecId;
            customsInvoiceRegnJournalLine.InventDimId                           = localPurchLine.InventDimId;
            customsInvoiceRegnJournalLine.CustomsInvoiceRegnJournalTableRecId   = customsInvoiceRegnJournalTable.RecId;
            customsInvoiceRegnJournalLine.Receive = CustomsInvoiceRegistrateForm_IN::calcReceiveQty(localPurchLine);

            select sum(Receive) from customsInvoiceRegnJournalLineLoc
                where customsInvoiceRegnJournalLineLoc.PurchLineRecId == localPurchLine.RecId;

            if (customsInvoiceRegnJournalLine.Receive != 0)
            {
                if (customsInvoiceRegnJournalLine.Receive + customsInvoiceRegnJournalLineLoc.Receive == localPurchLine.PurchQty)
                {
                    customsInvoiceRegnJournalLine.CustomsInvoiceRegnJournalTableRecId = customsInvoiceRegnJournalTableRecId;
                    customsInvoiceRegnJournalLine.insert();
                }
                customsInvoiceRegnJournalLine_IN_ds.research();
            }
        }
    }
    ttscommit;
}

Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement movement = InventMovement::construct(PurchLine::findRecId(customsInvoiceRegnJournalLine_IN.PurchLineRecId));

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default :
            throw error(strfmt("@SYS54195",funcname()));
    }
}

