void allowEdit()
{
    FormDataObject oneTimeCustomer = custAdvanceInvoiceTable_ds.object(fieldnum(CzCustAdvanceInvoiceTable,
                                                                                OneTimeCustomer));
    boolean creditNote;

    #ISOCountryRegionCodes    // Macro containing all ISO Codes

    if (custAdvanceInvoiceTable.Posted)
    {
        allowEditFieldsOnFormDS_W(custAdvanceInvoiceTable_ds, false);
        custAdvanceInvoiceTable_ds.allowDelete(false);

        buttonCustPostAdvanceInvoice.enabled(false);

        buttonStatusChange.enabled(true);
        buttonAdvanceInvoiceLink.enabled(custAdvanceInvoiceTable.Status != CzAdvanceInvoiceStatus::Closed);
        buttonCreditNote.enabled(false);

        groupAmount.allowEdit(false);
        header_CurrencyCode.allowEdit(false);

        printAdvanceInvoice_Copy.enabled(custAdvanceInvoiceTable.CustPostingProfile != '');
        printAdvanceInvoice_Duplicate.enabled(custAdvanceInvoiceTable.CustPostingProfile != '');
        printAdvanceInvoice_PrintMgmt.enabled(custAdvanceInvoiceTable.CustPostingProfile != '');
    }
    else
    {
        allowEditFieldsOnFormDS_W(custAdvanceInvoiceTable_ds, true);
        custAdvanceInvoiceTable_ds.allowDelete(true);

        buttonCustPostAdvanceInvoice.enabled(custAdvanceInvoiceTable.RecId);

        buttonStatusChange.enabled(false);
        buttonAdvanceInvoiceLink.enabled(SysCountryRegionCode::isLegalEntityInCountryRegion([#isoHU]));
        buttonCreditNote.enabled(custAdvanceInvoiceTable.RecId);

        groupAmount.allowEdit(true);
        header_CurrencyCode.allowEdit(true);

        printAdvanceInvoice_Copy.enabled(false);
        printAdvanceInvoice_Duplicate.enabled(false);
        printAdvanceInvoice_PrintMgmt.enabled(false);

        creditNote = custAdvanceInvoiceTable.creditNote();
    }

    element.allowEditLines();

    custAdvanceInvoiceTable_ds.object(fieldnum(CzCustAdvanceInvoiceTable, PostVAT)).allowEdit(true);
    custAdvanceInvoiceTable_ds.object(fieldnum(CzCustAdvanceInvoiceTable, PostVATDate)).allowEdit(custAdvanceInvoiceTable.PostVAT);
    custAdvanceInvoiceTable_ds.object(fieldnum(CzCustAdvanceInvoiceTable, TaxDocumentCreate)).allowEdit(custAdvanceInvoiceTable.PostVAT);

    custAdvanceInvoiceTable_ds.object(fieldnum(CzCustAdvanceInvoiceTable, PostingProfile)).allowEdit(true);
    custAdvanceInvoiceTable_ds.object(fieldnum(CzCustAdvanceInvoiceTable, CurrencyCode)).allowEdit(! creditNote);

    buttonSetSalesOrder.enabled (custAdvanceInvoiceTable.RecId && custAdvanceInvoiceTable.Status != CzAdvanceInvoiceStatus::Closed);
    buttonSetFreeInvoice.enabled(custAdvanceInvoiceTable.RecId && custAdvanceInvoiceTable.Status != CzAdvanceInvoiceStatus::Closed);

    buttonErrorMessage.enabled(custAdvanceInvoiceTable.Log ? true : false);

    salesTableButton.enabled        (custAdvanceInvoiceTable.SalesId             ? true : false);
    freeInvoiceButton.enabled       (custAdvanceInvoiceTable.FreeInvoiceRefRecId ? true : false);
    ledgerTransVoucherButton.enabled(custAdvanceInvoiceTable.Voucher             ? true : false);
    fiscalDocChange_PL.enabled      (custAdvanceInvoiceTable.Posted &&
                                     (custAdvanceInvoiceTable.FiscalDocState_PL == PlFiscalDocState::FiscalDocument ||
                                      custAdvanceInvoiceTable.FiscalDocState_PL == PlFiscalDocState::PrintedFiscalDocument));

    if (custAdvanceInvoiceTable.OneTimeCustomer || custAdvanceInvoiceTable.OrderAccount)
    {
        oneTimeCustomer.skip(true);
        oneTimeCustomer.allowEdit(false);
    }
    else
    {
        oneTimeCustomer.skip(false);
        oneTimeCustomer.allowEdit(true);
    }

    if (custAdvanceInvoiceTable.Triangulation)
    {
        exchRateControl.allowEdit(this.mayCurrencyBeModified());
        exchRateSecControl.allowEdit(this.maySecondaryCurrencyBeModified());
    }
    else
    {
        exchRateControl.allowEdit(true);
        exchRateSecControl.allowEdit(false);
    }

    custAdvanceInvoiceTable_ds.object(fieldnum(CzCustAdvanceInvoiceTable, Triangulation)).allowEdit(this.mayTriangulationBeModified());
}

void allowEditLines()
{
    if (custAdvanceInvoiceTable.Posted || !custAdvanceInvoiceTable.RecId)
    {
        custAdvanceInvoiceLine_ds.allowCreate(false);
        custAdvanceInvoiceLine_ds.allowEdit(false);
        custAdvanceInvoiceLine_ds.allowDelete(false);
    }
    else
    {
        custAdvanceInvoiceLine_ds.allowCreate(! custAdvanceInvoiceLine.AdvanceInvoiceLineInternalRef);

        if (custAdvanceInvoiceLine.AdvanceInvoiceLineInternalRef &&
            custAdvanceInvoiceLine.OrigAdvanceInvoiceLineRecId)
        {
            allowEditFieldsOnFormDS_W(custAdvanceInvoiceLine_ds, false);
            custAdvanceInvoiceLine_ds.object(fieldnum(CzCustAdvanceInvoiceLine, TaxGroup)).allowEdit(custAdvanceInvoiceTable_ds.allowEdit());
            custAdvanceInvoiceLine_ds.object(fieldnum(CzCustAdvanceInvoiceLine, TaxItemGroup)).allowEdit(custAdvanceInvoiceTable_ds.allowEdit());
        }
        else
        {
            custAdvanceInvoiceLine_ds.allowEdit(true);
            allowEditFieldsOnFormDS_W(custAdvanceInvoiceLine_ds, true);
        }
        custAdvanceInvoiceLine_ds.object(fieldnum(CzCustAdvanceInvoiceLine, CorrectionDescription)).allowEdit(custAdvanceInvoiceLine.AdvanceInvoiceLineInternalRef);

        custAdvanceInvoiceLine_ds.allowDelete(true);
    }
}

public boolean checkPrepaymentLink_W()
{
    ErrorTolerance prepaymentLink = CustParameters::find().PrepaymentLink_W;
    if (prepaymentLink == ErrorTolerance::Accept || ! custAdvanceInvoiceTable.Posted)
        return true;

    if (prepaymentLink                            == ErrorTolerance::Warning &&
        Box::yesNo("@GLS114096", DialogButton::No) == DialogButton::No)
        return checkFailed("@SYS107501");

    if (prepaymentLink == ErrorTolerance::Error)
        return checkFailed("@GLS114097");

    return true;
}

void init()
{
    SalesTable localSalesTable;

    if (element.args().record() &&
        element.args().record().TableId == tablenum(CzCustAdvanceInvoiceTable) &&
        element.args().callerName() != formstr(CzCustAdvanceInvoiceListPage))
    {
        fromOriginalDocuments = true;
    }

    companyCurrency    = CompanyInfoHelper::standardCurrency();
    triangulationCache = new Map(Types::Container, Types::Enum);

    super();

    element.initCurrencyControls();

    if (element.args().parmEnum() == NoYes::Yes)
    {
        if (element.args().record())
        {
            callerTableId = element.args().record().TableId;
        }
    }
    else
    {
        custAdvanceInvoiceTable_ds.insertIfEmpty(false);
        custAdvanceInvoiceLine_ds.insertIfEmpty(false);
    }

    statusDisplay.selection(CzAdvanceInvoiceStatusDisplay::All);
    element.statusFilterChanged();

    Grid_OffsetLedgerDimension.parmPostingType(LedgerPostingType::CustRevenue);

    // Needed to check validity of address
    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidFrom));
    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidTo));
    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));

    if (callerTableId == tableNum(SalesTable))
    {
        localSalesTable = element.args().record();

        if (!CzCustAdvanceInvoiceTable::existsSalesId(localSalesTable.SalesId))
        {
            custAdvanceInvoiceTable_ds.create();
        }
    }
}

private void initCurrencyControls()
{
    int             idx;
    FormRealControl curControl;

    for (idx = 1; idx <= groupAmount.controlCount(); idx ++)
    {
        if (groupAmount.controlNum(idx) is FormRealControl)
        {
            curControl = groupAmount.controlNum(idx);
            switch (curControl.dataMethod())
            {
                case tablemethodstr(CzCustAdvanceInvoiceTable, editExchRate)       :
                    exchRateControl    = curControl;
                    break;

                case tablemethodstr(CzCustAdvanceInvoiceTable, editExchRateSecond) :
                    exchRateSecControl = curControl;
                    break;
            }
        }
    }

    if (! exchRateControl ||
        ! exchRateSecControl)
        throw error(Error::wrongUseOfFunction(funcname()));
}

void initOrderAccountControl()
{
    int idx = 1;

    while (! orderAccountContol && idx <= element.design().controlCount())
    {
        element.loopCtrl(element.design().controlNum(idx));
        idx ++;
    }
}

void loopCtrl(Object _ctrl)
{
    int idx = 1;

    FormStringControl tmpControl;
    DictField         tmpFieldDict,
                      tmpControlDict;

    switch (classidget(_ctrl))
    {
        case classnum(FormStringControl)  :
            tmpControl = _ctrl;

            if (tmpControl.dataSource() == custAdvanceInvoiceTable_DS.id())
            {
                tmpControlDict = new DictField(tablenum(CzCustAdvanceInvoiceTable), tmpControl.dataField());
                tmpFieldDict   = new DictField(tablenum(CzCustAdvanceInvoiceTable), fieldnum(CzCustAdvanceInvoiceTable, OrderAccount));
                if (tmpControlDict.name() == tmpFieldDict.name())
                {
                    orderAccountContol = tmpControl;
                }
            }
            break;
        case classnum(FormGroupControl)   :
        case classnum(FormTabControl)     :
        case classnum(FormGridControl)    :
        case classnum(FormTabPageControl) :
            while (! orderAccountContol && idx <= _ctrl.controlCount())
            {
                element.loopCtrl(_ctrl.controlNum(idx));
                idx ++;
            }
            return;
    }
}

public boolean mayCurrencyBeModified()
{
    return ! this.triangulation(custAdvanceInvoiceTable.CurrencyCode, custAdvanceInvoiceTable.InvoiceDate);
}

public boolean maySecondaryCurrencyBeModified()
{
    boolean ok = false;

    if (this.triangulation(companyCurrency, custAdvanceInvoiceTable.InvoiceDate))
    {
        ok = false;
    }
    else
    {
        if (this.triangulation(custAdvanceInvoiceTable.CurrencyCode, custAdvanceInvoiceTable.InvoiceDate))
        {
            ok = true;
        }
    }

    return ok;
}

public boolean mayTriangulationBeModified()
{
    boolean ok = true;

    if (this.triangulation(companyCurrency, custAdvanceInvoiceTable.InvoiceDate))
    {
        ok = false;
    }

    if (!this.triangulation(companyCurrency, custAdvanceInvoiceTable.InvoiceDate) &&
        !this.triangulation(custAdvanceInvoiceTable.CurrencyCode, custAdvanceInvoiceTable.InvoiceDate))
    {
        ok = false;
    }

    return ok;
}

