}

void create(boolean append = false)
{
    SalesTable localSalesTable;

    custAdvanceInvoiceTable_ds.allowEdit(true);
    custAdvanceInvoiceTable_ds.allowCreate(true);
    custAdvanceInvoiceTable_ds.allowDelete(true);

    custAdvanceInvoiceTable_ds.object(fieldnum(CzCustAdvanceInvoiceTable, OneTimeCustomer)).skip(false);
    custAdvanceInvoiceTable_ds.object(fieldnum(CzCustAdvanceInvoiceTable, OneTimeCustomer)).allowEdit(true);

    super(append);

    if (callerTableId && ! inPrompt)
    {
        switch (callerTableId)
        {
            case tablenum(SalesTable)           :
                inPrompt = true;

                if (CzAdvanceInvoiceCreate::createFromForm(element.args().record(),
                                                       custAdvanceInvoiceTable,
                                                       custAdvanceInvoiceLine))
                {
                    custAdvanceInvoiceLine_ds.research();
                    custAdvanceInvoiceLine_ds.active();

                    custAdvanceInvoiceTable_ds.rereadReferenceDataSources();
                }
                else
                {
                    localSalesTable = element.args().record();

                    if (!CzCustAdvanceInvoiceTable::existsSalesId(localSalesTable.SalesId))
                    {
                        element.close();
                    }
                }

                inPrompt = false;

                break;
        }
    }

    element.allowEdit();
}

public void executeQuery()
{
    logisticsPostalAddress_ds.validTimeStateUpdate(ValidTimeStateUpdate::Correction);
    logisticsPostalAddress_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(), DateTimeUtil::maxValue());

    super();

    element.allowEdit();
}

