void write()
{
    ttsbegin;

    if (custAdvanceInvoiceTable.CurrencyCode != custAdvanceInvoiceTable.orig().CurrencyCode &&
        (CzCustAdvanceInvoiceLine::exist(custAdvanceInvoiceTable.RecId) ||
         MarkupTrans::exist(custAdvanceInvoiceTable.TableId,
                            custAdvanceInvoiceTable.RecId)
        )                                                                                   &&
        CzCustAdvanceInvoiceTable::promptConvertCurrencyCode())
    {
        custAdvanceInvoiceTable.convertCurrencyCode(custAdvanceInvoiceTable.CurrencyCode);
    }

    super();

    ttscommit;
}

