    {
        return;
    }

    super();

    setupContext.addHierarchyContext(PrintMgmtHierarchyType::Sales, PrintMgmtNodeType::SalesAdvanceInvoice);

    setupContext.parmCaller(element);
    setupContext.parmReferencedTableBuffer(custAdvanceInvoiceTable_ds.cursor());
    setupContext.parmCountryRegionId(custAdvanceInvoiceTable.custTable_InvoiceAccount().postalAddress().CountryRegionId);
    PrintMgmt::launchSetup(setupContext);
}

public void clicked()
{
    if (custAdvanceInvoiceTable)
    {
        Dialog dialog = new Dialog("@BusinessDocumentSubmissionService:EnterSubmissionUUIDDialogCaption");
        DialogField field = dialog.addField(extendedTypeStr(UUID), "@BusinessDocumentSubmissionService:SubmissionUUID");
        field.value(custAdvanceInvoiceTable.SubmissionUUID);
        dialog.run();
        if (dialog.closedOk())
        {
            custAdvanceInvoiceTable.SubmissionUUID = field.value();
            custAdvanceInvoiceTable_ds.write();
            custAdvanceInvoiceTable_ds.refresh();
        }
    }
    else
    {
        error(Error::missingFormActiveBuffer(formStr(CzCustAdvanceInvoice)));
    }
}

public void lookup()
{
    ContactPerson::lookupCustContactPerson(
        contactPersonId,
        custAdvanceInvoiceTable.OrderAccount,
        this,
        custAdvanceInvoiceTable.ContactPersonId);
}
