Method.Source:
    [Form]
    public class CzTaxDocumentTable extends FormRun
    {
        CzTaxDocumentFormEngine engine;
}

void enableTaxDocumentButtons()
{
    taxDocument_Print_Cust.visible(taxDocumentTable.RecId && taxDocumentTable.TransTableId == tablenum(CustTrans));
    taxDocument_Print_Vend.visible(! taxDocument_Print_Cust.visible());
    taxDocument_Print_Cust.enabled(taxDocumentTable.RecId);
    taxDocument_Print_Vend.enabled(taxDocumentTable.RecId);
}

public void init()
{
    super();

    engine = new CzTaxDocumentFormEngine();

    element.enableTaxDocumentButtons();
    element.enableTaxCreditMemoPrint();
}

public int active()
{
    int ret = super();

    element.enableTaxDocumentButtons();

    return ret;
}

public void init()
{
    QueryBuildDataSource    ds;
    QueryBuildRange         range;

    tableId                 transTableId;
    fieldId                 accountFieldId_Trans, accountFieldId_Table;

    super();

    ds = this.query().dataSourceTable(tablenum(CzTaxDocumentTable));
    ds.addSortField(fieldnum(CzTaxDocumentTable, TaxDocumentNum));

    if (element.args())
    {
        if (element.args().record())
        {
            switch (element.args().dataset())
            {
                case tablenum(CustTable) :
                    transTableId         = tablenum(CustTrans);
                    accountFieldId_Trans = fieldnum(CustTrans, AccountNum);
                    accountFieldId_Table = fieldnum(CustTable, AccountNum);
                    break;
                case tablenum(VendTable) :
                    transTableId         = tablenum(VendTrans);
                    accountFieldId_Trans = fieldnum(VendTrans, AccountNum);
                    accountFieldId_Table = fieldnum(VendTable, AccountNum);
                    break;
                case tablenum(CustTrans) :
                    transTableId         = tablenum(CustTrans);
                    accountFieldId_Trans = fieldnum(CustTrans, RecId);
                    accountFieldId_Table = fieldnum(CustTrans, RecId);
                    break;
                case tablenum(VendTrans) :
                    transTableId         = tablenum(VendTrans);
                    accountFieldId_Trans = fieldnum(VendTrans, RecId);
                    accountFieldId_Table = fieldnum(VendTrans, RecId);
                    break;
            }

            if (transTableId)
            {
                ds = this.query().dataSourceTable(tablenum(CzTaxDocumentTable));

                ds = ds.addDataSource(tablenum(CzTaxDocumentLink));
                ds.joinMode(JoinMode::ExistsJoin);
                ds.relations(true);

                ds = ds.addDataSource(transTableId);
                ds.joinMode(JoinMode::ExistsJoin);
                ds.relations(true);
                ds.addDynalink(accountFieldId_Trans, element.args().record(), accountFieldId_Table);
            }
        }

        if (element.args().parmEnumType() == enumnum(ModuleCustVend))
        {
            ds    = this.query().dataSourceTable(tablenum(CzTaxDocumentTable));
            range = ds.addRange(fieldnum(CzTaxDocumentTable, TransTableId));
            range.status(RangeStatus::Hidden);

            switch (element.args().parmEnum())
            {
                case ModuleCustVend::Cust :
                    range.value(SysQuery::value(tablenum(CustTrans)));
                    break;
                case ModuleCustVend::Vend :
                    range.value(SysQuery::value(tablenum(VendTrans)));
                    break;
            }
        }
    }
}

public int active()
{
    int ret = super();

    taxCreditMemo_DS.object(fieldnum(CzTaxCreditMemo,
                                     TaxCreditMemoNum)).allowEdit(! taxCreditMemo.TaxCreditMemoNum);
    element.enableTaxCreditMemoPrint();

    return ret;
}

public void executeQuery()
{
    super();

    taxCreditMemo_Cust_Print.visible(  taxDocumentTable.RecId && taxDocumentTable.TransTableId == tablenum(CustTrans));
    taxCreditMemo_Vend_Print.visible(! taxDocumentTable.RecId || taxDocumentTable.TransTableId == tablenum(VendTrans));
    element.enableTaxCreditMemoPrint();
}
