void allowEdit()
{
    FormDataObject oneTimeVendor = vendAdvanceInvoiceTable_ds.object(fieldnum(CzVendAdvanceInvoiceTable,
                                                                              OneTimeVendor));

    if (vendAdvanceInvoiceTable.Posted)
    {
        vendAdvanceInvoiceTable_ds.allowEdit(false);
        vendAdvanceInvoiceTable_ds.allowDelete(false);

        buttonVendPostAdvanceInvoice.enabled(false);

        buttonStatusChange.enabled(true);
        buttonAdvanceInvoiceLink.enabled(vendAdvanceInvoiceTable.Status != CzAdvanceInvoiceStatus::Closed);
    }
    else
    {
        vendAdvanceInvoiceTable_ds.allowEdit(true);
        vendAdvanceInvoiceTable_ds.allowDelete(true);

        buttonVendPostAdvanceInvoice.enabled(vendAdvanceInvoiceTable.RecId);

        buttonStatusChange.enabled(false);
        buttonAdvanceInvoiceLink.enabled(false);
    }

    element.allowEditLines();

    buttonSetPurchOrder.enabled(vendAdvanceInvoiceTable.RecId && vendAdvanceInvoiceTable.Status != CzAdvanceInvoiceStatus::Closed);

    buttonErrorMessage.enabled(vendAdvanceInvoiceTable.Log ? true : false);

    purchTableButton.enabled(vendAdvanceInvoiceTable.PurchId ? true : false);

    PaymentsLinkedButton.enabled(VendAdvanceInvoiceLink.RecId);

    if (vendAdvanceInvoiceTable.OneTimeVendor || vendAdvanceInvoiceTable.OrderAccount)
    {
        oneTimeVendor.skip(true);
        oneTimeVendor.allowEdit(false);
    }
    else
    {
        oneTimeVendor.skip(false);
        oneTimeVendor.allowEdit(true);
    }
}

void allowEditLines()
{
    if (vendAdvanceInvoiceTable.Posted || !vendAdvanceInvoiceTable.RecId)
    {
        vendAdvanceInvoiceLine_ds.allowCreate(false);
        vendAdvanceInvoiceLine_ds.allowEdit(false);
        vendAdvanceInvoiceLine_ds.allowDelete(false);
    }
    else
    {
        vendAdvanceInvoiceLine_ds.allowEdit(true);
        vendAdvanceInvoiceLine_ds.allowCreate(true);
        vendAdvanceInvoiceLine_ds.allowDelete(true);
    }
}

void init()
{
    super();

    if (element.args().parmEnum() == NoYes::Yes)
    {
        callerTableId = element.args().dataset();

        if (callerTableId == tableNum(PurchTable) && element.args().record())
        {
            PurchTable  localPurchTable = element.args().record();

            if (!CzVendAdvanceInvoiceTable::existsPurchId(localPurchTable.PurchId))
            {
                vendAdvanceInvoiceTable_ds.create();
            }
        }
    }
    else
    {
        vendAdvanceInvoiceTable_ds.insertIfEmpty(false);
        vendAdvanceInvoiceLine_ds.insertIfEmpty(false);
    }

    statusDisplay.selection(CzAdvanceInvoiceStatusDisplay::All);
    element.statusFilterChanged();

    // Needed to check validity of address
    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidFrom));
    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidTo));
    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));
}

void initOrderAccountControl()
{
    for (int idx = 1; ! orderAccountContol && idx <= element.design().controlCount(); idx++)
    {
        element.loopCtrl(element.design().controlNum(idx));
    }
}

void loopCtrl(Object _ctrl)
{
    int idx = 1;

    FormStringControl tmpControl;
    DictField         tmpFieldDict,
                      tmpControlDict;

    switch (classidget(_ctrl))
    {
        case classnum(FormStringControl)  :
            tmpControl = _ctrl;

            if (tmpControl.dataSource() == vendAdvanceInvoiceTable_DS.id())
            {
                tmpControlDict = new DictField(tablenum(CzVendAdvanceInvoiceTable),
                                               tmpControl.dataField());
                tmpFieldDict   = new DictField(tablenum(CzVendAdvanceInvoiceTable),
                                               fieldnum(CzVendAdvanceInvoiceTable,
                                                        OrderAccount));
                if (tmpControlDict.name() == tmpFieldDict.name())
                {
                    orderAccountContol = tmpControl;
                }
            }
            break;
        case classnum(FormGroupControl)   :
        case classnum(FormTabControl)     :
        case classnum(FormGridControl)    :
        case classnum(FormTabPageControl) :
            while (! orderAccountContol && idx <= _ctrl.controlCount())
            {
                element.loopCtrl(_ctrl.controlNum(idx));
                idx ++;
            }
    }
}

public void run()
{
    super();

    if (element.args().formViewOption() == FormViewOption::Grid ||
        (vendAdvanceInvoiceTable_ds.numberOfRowsLoaded() > 0 &&
            element.args().record() &&
            element.args().record().TableId == tableNum(PurchTable)))
    {
         tabPageGrid.activatePage();
    }
    else
    {
        tabPageDetails.activatePage();

        if ( element.args().parmEnum() == NoYes::Yes &&
            element.args().dataset() != tableNum(PurchTable))
        {
            headerSummaryTabPage.expand();
        }
    }
}

public void setTitleText()
{
    if (vendAdvanceInvoiceTable.InvoiceId)
    {
        advanceInvoiceId.text(strFmt('%1 : %2 - %3', vendAdvanceInvoiceTable.InvoiceId,
                                                vendAdvanceInvoiceTable.InvoiceAccount,
                                                vendAdvanceInvoiceTable.vendTable_InvoiceAccount().name()));
    }
    else
    {
        advanceInvoiceId.text(strFmt('%1 - %2', vendAdvanceInvoiceTable.InvoiceAccount,
                                                vendAdvanceInvoiceTable.vendTable_InvoiceAccount().name()));
    }
}

void statusFilterChanged()
{
    switch (statusDisplay.selection())
    {
        case CzAdvanceInvoiceStatusDisplay::All       :
            statusRange.value(SysQuery::valueUnlimited());
            break;

        case CzAdvanceInvoiceStatusDisplay::Open      :
            statusRange.value(SysQuery::value(CzAdvanceInvoiceStatus::Open));
            break;

        case CzAdvanceInvoiceStatusDisplay::NonClosed :
            statusRange.value(queryRangeConcat(SysQuery::value(CzAdvanceInvoiceStatus::Open),
                                               SysQuery::value(CzAdvanceInvoiceStatus::PartPaid)));
            break;

        case CzAdvanceInvoiceStatusDisplay::Paid      :
            statusRange.value(queryRangeConcat(SysQuery::value(CzAdvanceInvoiceStatus::Closed),
                                               SysQuery::value(CzAdvanceInvoiceStatus::PartPaid)));
            break;

        case CzAdvanceInvoiceStatusDisplay::Closed    :
            statusRange.value(SysQuery::value(CzAdvanceInvoiceStatus::Closed));
            break;
    }
}

int active()
{
    int ret = super();

    element.allowEdit();

    vendAdvanceInvoiceLink_ds.executeQuery();

    if (vendAdvanceInvoiceTable.InvoiceDate)
    {
        DimensionEntryControlHeader.parmNonActiveValueErrorTolerance(ErrorTolerance::Warning);
        DimensionEntryControlHeader.parmActiveValueFilterDate(vendAdvanceInvoiceTable.InvoiceDate);
    }

    element.setTitleText();

    return ret;
}

void create(boolean append = false)
{
    vendAdvanceInvoiceTable_ds.allowEdit(true);
    vendAdvanceInvoiceTable_ds.allowCreate(true);
    vendAdvanceInvoiceTable_ds.allowDelete(true);

    vendAdvanceInvoiceTable_ds.object(fieldnum(CzVendAdvanceInvoiceTable, OneTimeVendor)).skip(false);
    vendAdvanceInvoiceTable_ds.object(fieldnum(CzVendAdvanceInvoiceTable, OneTimeVendor)).allowEdit(true);

    super(append);

    if (callerTableId && !inPrompt)
    {
        switch (callerTableId)
        {
            case tablenum(PurchTable):
                inPrompt = true;

                if (CzAdvanceInvoiceCreate::createFromForm(element.args().record(),
                                                       vendAdvanceInvoiceTable,
                                                       vendAdvanceInvoiceLine))
                {
                    vendAdvanceInvoiceLine_ds.research();
                    vendAdvanceInvoiceLine_ds.active();

                    vendAdvanceInvoiceTable_ds.rereadReferenceDataSources();
                }

                inPrompt = false;

                break;
        }
    }

    element.allowEdit();
}

public void executeQuery()
{
    logisticsPostalAddress_ds.validTimeStateUpdate(ValidTimeStateUpdate::Correction);
    logisticsPostalAddress_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(), DateTimeUtil::maxValue());

    super();

    element.allowEdit();
}

