void invoiceDateModified()
{
    vendAdvanceInvoiceTable_ds.paymentModified();

    vendAdvanceInvoiceTable_ds.refresh();
    vendAdvanceInvoiceLine_ds.allowCreate(!vendAdvanceInvoiceTable.InvoiceAccount);
}

void paymentModified()
{
    vendAdvanceInvoiceTable.initFromPayment();
    vendAdvanceInvoiceTable.DueDate = dateNull();
    vendAdvanceInvoiceTable.calcDue();
}

void write()
{
    ttsbegin;

    if (vendAdvanceInvoiceTable.CurrencyCode != vendAdvanceInvoiceTable.orig().CurrencyCode &&
        (CzVendAdvanceInvoiceLine::exist(vendAdvanceInvoiceTable.RecId) ||
         MarkupTrans::exist(vendAdvanceInvoiceTable.TableId,
                            vendAdvanceInvoiceTable.RecId))                                 &&
        CzVendAdvanceInvoiceTable::promptConvertCurrencyCode())
    {
        vendAdvanceInvoiceTable.convertCurrencyCode(vendAdvanceInvoiceTable.CurrencyCode);
    }

    super();

    ttscommit;
}

public void modified()
{
    super();

    // Reload default dimensions as the specification of the customer account loads new defaults
    if (vendAdvanceInvoiceTable.InvoiceDate)
    {
        DimensionEntryControlHeader.parmNonActiveValueErrorTolerance(ErrorTolerance::Warning);
        DimensionEntryControlHeader.parmActiveValueFilterDate(vendAdvanceInvoiceTable.InvoiceDate);
    }

    vendAdvanceInvoiceTable_ds.cacheCalculateMethod(tablemethodstr(CzVendAdvanceInvoiceTable, displayInvoiceAccountName));

    vendAdvanceInvoiceTable.initRecord();
    vendAdvanceInvoiceTable_ds.refresh();

    vendAdvanceInvoiceLine_ds.allowCreate(true);
}

