void clicked()
{
    PrintMgmtSetupContext setupContext = new PrintMgmtSetupContext();

    if (vendAdvanceInvoiceTable_ds.cursor() == null)
    {
        return;
    }

    super();

    setupContext.addHierarchyContext(PrintMgmtHierarchyType::Purch, PrintMgmtNodeType::PurchaseAdvanceInvoice);

    setupContext.parmCaller(element);
    setupContext.parmReferencedTableBuffer(vendAdvanceInvoiceTable_ds.cursor());
    setupContext.parmCountryRegionId(vendAdvanceInvoiceTable.vendTable_InvoiceAccount().postalAddress().CountryRegionId);
    PrintMgmt::launchSetup(setupContext);
}

