[Form]
public class CzVendAdvanceInvoiceLink extends FormRun
{
    CzVendAdvanceInvoiceTable callerInvoiceTable;
    VendTrans                 callerVendTrans;
    LedgerJournalTrans        callerLedgerJournalTrans;

    CzAdvanceInvoiceLink      invoiceLink;
    container                 sharedServiceCompanies;

}

edit NoYes changeInvoiceStatus(boolean _set, NoYes _changeStatus)
{
    NoYes ret;

    if (_set)
    {
        ret = invoiceLink.parmUpdateInvoiceStatus(_changeStatus);
        invoiceLink.setDefaultStatus();
        vendAdvanceInvoiceTable_NewStatus.visible(_changeStatus);
        header_VendAdvanceInvoiceTable_NewStatus.visible(_changeStatus);
    }
    else
    {
        ret = invoiceLink.parmUpdateInvoiceStatus();
    }

    return ret;
}

public void closeOk()
{
    FormDataSource fds;

    super();

    invoiceLink.updateLink();

    if (callerInvoiceTable &&
        FormDataUtil::isFormDataSource(element.args().record()))
    {
        fds = FormDataUtil::getFormDataSource(element.args().record());
        fds.executeQuery();
        fds.positionToRecord(callerInvoiceTable);
    }
}

public void init()
{
    CompanyId   callerCompany   = curext();
    Imagelist   list            = CzAdvanceInvoiceLink::imageList();

    if (! element.args().record())
        throw error(strfmt("@SYS22678", element.name()));

    switch (element.args().dataset())
    {
        case tablenum(CzVendAdvanceInvoiceTable) :
            callerInvoiceTable       = element.args().record().data();
            callerCompany            = callerInvoiceTable.company();
            invoiceLink              = CzAdvanceInvoiceLink::newFromAdvance(callerInvoiceTable);
            break;

        case tablenum(VendTrans)                 :
            callerVendTrans          = element.args().record().data();
            callerCompany            = callerVendTrans.company();
            invoiceLink              = CzAdvanceInvoiceLink::newFromPosted(callerVendTrans);
            break;

        case tablenum(LedgerJournalTrans)        :
            callerLedgerJournalTrans = element.args().record().data();
            callerCompany            = callerLedgerJournalTrans.Company;
            invoiceLink              = CzAdvanceInvoiceLink::newFromNonPosted(callerLedgerJournalTrans);
            break;
    }

    super();

    allowEditFieldsOnFormDS_W(ledgerJournalTrans_DS,      false);
    allowEditFieldsOnFormDS_W(vendAdvanceInvoiceTable_DS, false);
    allowEditFieldsOnFormDS_W(vendTrans_DS,               false);

    if (isConfigurationkeyEnabled(configurationkeynum(LedgerAdvIntercompanyAccounting)))
    {
        sharedServiceCompanies = CustVendOpenTransManager::findSharedServiceCompanies(callerCompany);
    }
    else
    {
        sharedServiceCompanies = [ callerCompany ];
    }

    element.setCompanyRange(ledgerJournalTrans_DS.query());
    element.setCompanyRange(vendAdvanceInvoiceTable_DS.query());
    element.setCompanyRange(vendTrans_DS.query());

    element.initFromInvoiceTable();
    element.initFromNonPosted();
    element.initFromPosted();

    ledgerJournalTrans_AlreadyLinked.imageList(list);
    vendAdvanceInvoiceTable_AlreadyLinked.imageList(list);
    vendTrans_AlreadyLinked.imageList(list);
}

#localmacro.queryRangeMask
    '((\%1 == \%3) && (\%2 == "\%4"))'
#endmacro

void initFromInvoiceTable()
{
    QueryBuildDataSource    qbds;
    QueryBuildDataSource    qbdsVendTable;
    QueryBuildRange         qbr;

    VendTable               vendTable;
    VendAccount             vendAccount;
    CompanyId               company;
    int                     idx;
    ;

    if (! callerInvoiceTable)
        return;

    vendTable = VendTable::find(callerInvoiceTable.InvoiceAccount);

    advanceInvoiceHeaderGroup.visible(true);
    advanceInvoiceTab.visible(false);

    ledgerJournalTransHeaderGroup.visible(false);
    ledgerJournalTransTab.visible(true);

    vendTransHeaderGroup.visible(false);
    vendTransGroup.visible(true);

    // LedgerJournalTrans datasource //
    qbds = ledgerJournalTrans_DS.query().dataSourceTable(tablenum(LedgerJournalTrans));

    findOrCreateRange_W(qbds,
                        fieldnum(LedgerJournalTrans,
                                 AccountType),
                        queryValue(LedgerJournalACType::Vend)).status(RangeStatus::Locked);

    for (idx = 1; idx <= conlen(sharedServiceCompanies); idx++)
    {
        company = conpeek(sharedServiceCompanies, idx);
        changecompany(company)
        {
            vendAccount = VendTable::findByPartyRecId(vendTable.Party).AccountNum;
        }

        if (vendAccount)
        {
            qbr = qbds.addRange(fieldnum(LedgerJournalTrans, LedgerDimension));
            qbr.status(RangeStatus::Hidden);
            qbr.value(strfmt(#queryRangeMask,
                             fieldstr(LedgerJournalTrans, LedgerDimension),
                             fieldstr(LedgerJournalTrans, Company),
                             LedgerDynamicAccountHelper::getDynamicAccountFromAccountNumber(vendAccount, LedgerJournalACType::Vend),
                             company));
        }
    }

    findOrCreateRange_W(qbds,
                        fieldnum(LedgerJournalTrans,
                                 Prepayment),
                        queryValue(NoYes::Yes)).status(RangeStatus::Locked);

    qbds = qbds.addDataSource(tablenum(LedgerJournalTable));
    qbds.relations(true);
    qbds.joinMode(JoinMode::ExistsJoin);

    qbds.addRange(fieldnum(LedgerJournalTable, Posted)).value(queryValue(NoYes::No));
    qbds.addRange(fieldnum(LedgerJournalTable, JournalType)).value(queryValue(LedgerJournalType::Payment));

    // CzVendAdvanceInvoiceTable datasource //
    qbds = vendAdvanceInvoiceTable_DS.query().dataSourceTable(tablenum(CzVendAdvanceInvoiceTable));

    qbds.clearDynalinks();

    findOrCreateRange_W(qbds,
                        fieldnum(CzVendAdvanceInvoiceTable,
                                 RecId),
                        queryValue(callerInvoiceTable.RecId)).status(RangeStatus::Locked);

    // VendTrans datasource //
    qbds = vendTrans_DS.query().dataSourceTable(tablenum(VendTrans));

    qbdsVendTable = qbds.addDataSource(tablenum(VendTable));
    qbdsVendTable.relations(false);

    qbdsVendTable.addLink(fieldnum(VendTrans, AccountNum), fieldnum(VendTable, AccountNum));
    qbdsVendTable.addLink(fieldnum(VendTrans, DataAreaId), fieldnum(VendTable, DataAreaId));
    qbdsVendTable.joinMode(JoinMode::ExistsJoin);
    qbdsVendTable.fetchMode(0);

    findOrCreateRange_W(qbdsVendTable,
                        fieldnum(VendTable, Party),
                        SysQuery::value(vendTable.Party)).status(RangeStatus::Locked);

    findOrCreateRange_W(qbds,
                        fieldnum(VendTrans,
                                 Prepayment),
                        queryValue(NoYes::Yes)).status(RangeStatus::Locked);

    findOrCreateRange_W(qbds,
                        fieldnum(VendTrans,
                                 Invoice),
                        SysQuery::valueEmptyString()).status(RangeStatus::Locked);

    findOrCreateRange_W(qbds,
                        fieldnum(VendTrans,
                                 Closed),
                        SysQuery::valueEmptyString()).status(RangeStatus::Locked);
}

void initFromNonPosted()
{
    QueryBuildDataSource    qbds;
    QueryBuildDataSource    qbdsVendTable;
    VendTable               vendTable;

    if (! callerLedgerJournalTrans)
        return;

    vendTable = VendTable::findByCompany(callerLedgerJournalTrans.Company,
                                         callerLedgerJournalTrans.parmAccount());

    advanceInvoiceHeaderGroup.visible(false);
    advanceInvoiceTab.visible(true);

    ledgerJournalTransHeaderGroup.visible(true);
    ledgerJournalTransTab.visible(false);

    vendTransHeaderGroup.visible(false);
    vendTransGroup.visible(false);

    // LedgerJournalTrans datasource //
    qbds = ledgerJournalTrans_DS.query().dataSourceTable(tablenum(LedgerJournalTrans));

    qbds.clearDynalinks();

    findOrCreateRange_W(qbds,
                        fieldnum(LedgerJournalTrans,
                                 RecId),
                        queryValue(callerLedgerJournalTrans.RecId)).status(RangeStatus::Locked);

    // CzCustAdvanceInvoiceTable datasource //
    qbds = vendAdvanceInvoiceTable_DS.query().dataSourceTable(tablenum(CzVendAdvanceInvoiceTable));

    qbdsVendTable = qbds.addDataSource(tablenum(VendTable));
    qbdsVendTable.relations(false);

    qbdsVendTable.addLink(fieldnum(CzCustAdvanceInvoiceTable, InvoiceAccount), fieldnum(VendTable, AccountNum));
    qbdsVendTable.addLink(fieldnum(CzCustAdvanceInvoiceTable, DataAreaId), fieldnum(VendTable, DataAreaId));
    qbdsVendTable.joinMode(JoinMode::ExistsJoin);
    qbdsVendTable.fetchMode(0);

    findOrCreateRange_W(qbdsVendTable,
                        fieldnum(VendTable, Party),
                        SysQuery::value(vendTable.Party)).status(RangeStatus::Locked);

    findOrCreateRange_W(qbds,
                        fieldnum(CzVendAdvanceInvoiceTable,
                                 Posted),
                        queryValue(NoYes::Yes)).status(RangeStatus::Locked);

    findOrCreateRange_W(qbds,
                        fieldnum(CzVendAdvanceInvoiceTable,
                                 Status),
                        SysQuery::valueNot(CzAdvanceInvoiceStatus::Closed)).status(RangeStatus::Locked);
}

