void setCompanyRange(Query _query)
{
    int i;
    ;

    _query.clearCompanyRange();

    for (i = 1; i <= conlen(sharedServiceCompanies); i++)
    {
        _query.addCompanyRange(conpeek(sharedServiceCompanies, i));
    }
}

display VendInvoiceAccount vendInvoiceAccount()
{
    VendInvoiceAccount ret;

    if (callerInvoiceTable)
    {
        ret = callerInvoiceTable.InvoiceAccount;
    }

    if (callerLedgerJournalTrans)
    {
        ret = callerLedgerJournalTrans.parmAccount();
    }

    if (callerVendTrans)
    {
        ret = callerVendTrans.AccountNum;
    }

    return ret;
}

edit NoYes markTrans(boolean _set, LedgerJournalTrans _trans, NoYes _mark)
{
    NoYes ret;

    if (_set)
    {
        invoiceLink.includePayment(_trans.company(), _trans.Voucher, _trans.TransDate, _mark);
        invoiceLink.setDefaultStatus();
        ret = _mark;
    }
    else
    {
        ret = invoiceLink.includePayment(_trans.company(), _trans.Voucher, _trans.TransDate);
    }

    return ret;
}

public int active()
{
    int ret = super();

    this.allowStatusChange();

    return ret;
}

edit NoYes markTrans(boolean _set, CzVendAdvanceInvoiceTable _trans, NoYes _mark)
{
    NoYes ret;

    if (_set)
    {
        invoiceLink.includeAdvance(_trans, _mark);
        invoiceLink.setDefaultStatus(_trans);
        this.allowStatusChange();
        ret = _mark;
    }
    else
    {
        ret = invoiceLink.includeAdvance(_trans);
    }

    return ret;
}

edit CzAdvanceInvoiceStatus newStatus(boolean                   _set,
                                      CzVendAdvanceInvoiceTable _trans,
                                      CzAdvanceInvoiceStatus    _newStatus)
{
    CzAdvanceInvoiceStatus ret;

    if (_set)
    {
        invoiceLink.status(_trans, _newStatus);
        ret = _newStatus;
    }
    else
    {
        ret = invoiceLink.status(_trans);
    }

    return ret;
}

display ImageRes transLinked(CzVendAdvanceInvoiceTable _trans)
{
    int ret = 1;

    if (callerLedgerJournalTrans &&
        CzVendAdvanceInvoiceLink::existsOtherPayment(_trans.company(),
                                                     _trans.RecId,
                                                     callerLedgerJournalTrans.company(),
                                                     callerLedgerJournalTrans.Voucher,
                                                     callerLedgerJournalTrans.TransDate))
    {
        ret = 0;
    }

    if (callerVendTrans &&
        CzVendAdvanceInvoiceLink::existsOtherPayment(_trans.company(),
                                                     _trans.RecId,
                                                     callerVendTrans.company(),
                                                     callerVendTrans.Voucher,
                                                     callerVendTrans.TransDate))
    {
        ret = 0;
    }

    return ret;
}

edit NoYes markTrans(boolean _set, VendTrans _trans, NoYes _mark)
{
    NoYes ret;

    if (_set)
    {
        invoiceLink.includePayment(_trans.company(), _trans.Voucher, _trans.TransDate, _mark);
        invoiceLink.setDefaultStatus();
        ret = _mark;
    }
    else
    {
        ret = invoiceLink.includePayment(_trans.company(), _trans.Voucher, _trans.TransDate);
    }

    return ret;
}

display ImageRes transLinked(VendTrans _trans)
{
    CzVendAdvanceInvoiceLink link;

    select firstonly RecId from link
        where link.Voucher             == _trans.Voucher   &&
              link.TransDate           == _trans.TransDate &&
              link.AdvanceInvoiceRecId != callerInvoiceTable.RecId;

    return link.RecId ? 0 : 1;
}
