/// <summary>
/// Applies effective date filter selected.
/// </summary>
/// <param name="_filterOptions">
/// Date filter option parameter.
/// </param>
/// <param name="_showAsOfDate">
/// Effective date for a filter.
/// </param>
/// <param name="_datasource">
/// Form data source object to apply filter.
/// </param>
/// <param name="_dataquery">
/// Form data query object to reset valid valid time state on.
/// </param>
public void applyFilter(DateFilterOptions _filterOptions, TransDate _showAsOfDate, Object _datasource, Object _dataquery)
{
    date maximumDate, minimumDate;

    minimumdate = dateNull();
    maximumDate = maxDate();

    filterOptions = _filterOptions;

    switch (filterOptions)
    {
        case DateFilterOptions::AsOf:
            _datasource.validTimeStateAutoQuery(ValidTimeStateAutoQuery::AsOfDate);
            _dataquery.resetValidTimeStateQueryType();
            break;

        case DateFilterOptions::AsOfAndRange:
            _datasource.validTimeStateAutoQuery(ValidTimeStateAutoQuery::AsOfDate);
            _dataquery.validTimeStateAsOfDate(_showAsOfDate);
            break;

        case DateFilterOptions::Range:
            _datasource.validTimeStateAutoQuery(ValidTimeStateAutoQuery::DateRange);
            _dataquery.validTimeStateDateRange(minimumDate, maximumDate);
            break;
    }

    _datasource.executeQuery();
}

private void initializeControlsToEnableMultiDeletion()
{
    FormBuildCommandButtonControl expireProcurementCategoryControl = this.form().design().control(formControlStr(DirPartyEcoResCategory, ExpireProcurementCategory));
    expireProcurementCategoryControl.autoRefreshData(false);
    expireProcurementCategoryControl.saveRecord(true);

    FormBuildButtonGroupControl procurementButtonGroupControl = this.form().design().control(formControlStr(DirPartyEcoResCategory, ProcurementButtonGroup));
    procurementButtonGroupControl.dataSource(formDataSourceStr(DirPartyEcoResCategory, VendCategory));

    FormBuildFunctionButtonControl procurementCategoryStatusVersionControl = this.form().design().control(formControlStr(DirPartyEcoResCategory, ProcurementCategoryStatusVersion));
    procurementCategoryStatusVersionControl.dataSource(formDataSourceStr(DirPartyEcoResCategory, VendProcurementCategoryStatus));
}

public void init()
{
    this.initializeControlsToEnableMultiDeletion();

    Common  emptyTitleDataSource = null;

    if (!element.args() || !element.args().caller())
    {
        throw error("@SYS22539");
    }

    showProcurement = false;

    switch (element.args().dataset())
    {
        case tableNum(VendTable):
            vendTable = element.args().record();
            partyRecId = vendTable.Party;
            showProcurement = true;
            break;
        case tableNum(DirPartyTable):
            partyRecId = element.args().record().RecId;
            break;
        default:
            throw error(strFmt("@SYS31187",element.args().dataset()));
    }

    super();

    if (vendTable.RecId == 0)
    {
        vendTable = VendTable::findByPartyRecId(partyRecId);
    }

    element.args().record(emptyTitleDataSource);
    element.design().caption(strFmt("@SYS335336", vendTable.AccountNum));
}

display DirPartyName partyName()
