    DirPartyName ret = DirPartyTable::findRec(partyRecId).Name;
    return ret;
}

/// <summary>
/// Refreshes the VendCategory table and VendProcurementCategoryStatus table data sources.
/// </summary>
/// <remarks>
/// Called by child forms when closing due to possibly changed data contained in these two tables.
/// </remarks>
public void refreshDataSources()
{
    int pos;

    // Reread the vendCategory which will reread the vendProcurementCategoryStatus. Retain the current row selected on both tables
    pos = vendCategory_ds.getPosition();
    vendCategory_ds.executeQuery();
    vendCategory_ds.setPosition(pos);

    pos = dirPartyEcoResCategory_ds.getPosition();
    dirPartyEcoResCategory_ds.executeQuery();
    dirPartyEcoResCategory_ds.setPosition(pos);
}

private boolean confirmRecordRemoving(FormDataSource _formDataSource)
{
    boolean ret;

    if (_formDataSource.anyMarked())
    {
        ret = Box::yesNo("@SCM:AreYouSureYouWantToExpireAllMarkedRecords", DialogButton::No) == DialogButton::Yes;
    }
    else
    {
        ret = Box::yesNo(strFmt("@SCM:YouAreAoutToExpireRecord", tableId2PName(_formDataSource.table())), DialogButton::No) == DialogButton::Yes;
    }

    return ret;
}

public void removeCategory()
{
    if (this.confirmRecordRemoving(dirPartyEcoResCategory_ds))
    {
        MultiSelectionHelper multiSelectionHelper = MultiSelectionHelper::construct();
        multiSelectionHelper.parmDatasource(dirPartyEcoResCategory_ds);
        DirPartyEcoResCategory dirPartyEcoResCategoryLocal = multiSelectionHelper.getFirst();

        ttsbegin;
        while (dirPartyEcoResCategoryLocal)
        {
            dirPartyEcoResCategoryLocal.delete();
            dirPartyEcoResCategoryLocal = multiSelectionHelper.getNext();
        }
        ttscommit;

        dirPartyEcoResCategory_ds.executeQuery();
    }
}

public void removeVendCategoryStatus()
{
    if (this.confirmRecordRemoving(vendCategory_ds))
    {
        MultiSelectionHelper multiSelectionHelper = MultiSelectionHelper::construct();
        multiSelectionHelper.parmDatasource(vendCategory_ds);
        VendCategory vendCategoryLocal = multiSelectionHelper.getFirst();

        ttsbegin;
        while (vendCategoryLocal)
        {
            VendProcurementCategoryStatus vendProcurementCategoryStatusLocal = vendCategoryLocal.joinChild();

            if (vendProcurementCategoryStatusLocal.validateDelete())
            {
                vendProcurementCategoryStatusLocal.delete();
            }

            vendCategoryLocal = multiSelectionHelper.getNext();
        }
        ttscommit;

        vendCategory_ds.executeQuery();
    }
}

public void delete()
{
    // vendor to category relation should be expired
    dirPartyEcoResCategory.expireActiveRecord(dirPartyEcoResCategory.Party, dirPartyEcoResCategory.Category);
    element.refreshDataSources();
}

