public void performFormLookup(xFormRun _form, FormControl _formControl)
{
    FormDataSource          formDataSource;
    Query                   query;
    QueryBuildDataSource    queryDataSource;

    formDataSource  = _form.objectSet();
    query           = formDataSource.query();
    queryDataSource = query.dataSourceTable(tableNum(EcoResCategoryHierarchy)).addDataSource(tableNum(EcoResCategoryHierarchyRole));
    queryDataSource.relations(true);
    queryDataSource.joinMode(JoinMode::NoExistsJoin);
    queryDataSource.addRange(fieldNum(EcoResCategoryHierarchyRole, NamedCategoryHierarchyRole)).value(SysQuery::value(EcoResCategoryNamedHierarchyRole::Procurement));
    super(_form, _formControl);
}

