public int active()
{
    int ret;
    date currentDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
    ret = super();

    if (vendProcurementCategoryStatus.ValidTo <= currentDate)
    {
        vendCategory_ds.allowEdit(false);
        vendProcurementCategoryStatus_ds.allowEdit(false);
    }
    else
    {
        vendCategory_ds.allowEdit(true);
        vendProcurementCategoryStatus_ds.allowEdit(true);
        vendProcurementCategoryStatus.validTimeStateUpdateMode(ValidTimeStateUpdate::CreateNewTimePeriod);
        vendCategory_ds.object(fieldNum(VendCategory, Category)).allowEdit(!vendProcurementCategoryStatus.RecId);
    }

    return ret;
}

