public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    Common ret;
    EcoResCategoryHierarchy     categoryHierarchy;
    EcoResCategoryHierarchyRole categoryHierarchyRole;

    select firstonly * from categoryHierarchy join categoryHierarchyRole
        where categoryHierarchy.RecId                           == categoryHierarchyRole.CategoryHierarchy
            && categoryHierarchyRole.NamedCategoryHierarchyRole == EcoResCategoryNamedHierarchyRole::Procurement;

    ret = EcoResCategory::resolveCategoryHierarchy(_formReferenceControl,  categoryHierarchy);

    return ret.RecId? ret : null;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common ret;
    EcoResCategoryHierarchy     categoryHierarchy;
    EcoResCategoryHierarchyRole categoryHierarchyRole;

    select firstonly * from categoryHierarchy join categoryHierarchyRole
        where categoryHierarchy.RecId == categoryHierarchyRole.CategoryHierarchy
            && categoryHierarchyRole.NamedCategoryHierarchyRole == EcoResCategoryNamedHierarchyRole::Procurement;

    ret = EcoResCategory::lookupCategoryHierarchy(_formReferenceControl,
                                                    categoryHierarchy);

    return ret;
}

public void write()
{
    VendCategory vendCategoryLocal;

    if (!vendCategory.RecId && vendCategory.validateWrite())
    {
        vendCategoryLocal = VendCategory::findOrCreateVendorCategory( vendCategory.VendorAccount,
                                                                  vendCategory.Category,
                                                                  vendCategory.VendorDataArea, true);
        categoryRecId = vendCategoryLocal.RecId;
        vendProcurementCategoryStatus.VendorCategory = categoryRecId;
    }

    super();

    if (vendCategoryLocal)
    {
        vendCategory.data(vendCategoryLocal);
        vendCategory.reread();
    }
}

public boolean validateWrite()
{
    boolean ret;

    if (!vendCategory.RecId)
    {
        ret = true;
    }
    else
    {
        ret = super();
    }

    return ret;
}

