public void clicked()
{
    super();
    ShowAsOfDateControlProcurement.dateValue(dateNull());
    ShowAsOfDateControlProcurement.allowEdit(!this.value());

    // if checkbox mark, we only show the current active records; if false, we show all.
    if (this.value())
    {
        element.applyFilter(DateFilterOptions::AsOf, dateNull(), vendCategory_ds, vendCategory_q);
    }
    else
    {
        element.applyFilter(DateFilterOptions::Range, dateNull(), vendCategory_ds, vendCategory_q);
    }
}

public boolean modified()
{
    super();

    // if we select a date, we only show the records as of that date; if date control is empty, we show all.
    if (this.dateValue())
    {
        element.applyFilter(DateFilterOptions::AsOfAndRange, this.dateValue(), vendCategory_ds, vendCategory_q);
    }
    else
    {
        element.applyFilter(DateFilterOptions::Range, dateNull(), vendCategory_ds, vendCategory_q);
    }

    return true;
}

