}

/// <summary>
/// Opens a form in New mode and creates a new record.
/// </summary>
/// <param name = "_menuItemName">The name of the menu item for the form.</param>
/// <param name = "_dataSourceName">The name of the data source to create a record on.</param>
/// <returns></returns>
private FormRun openFormAndCreate(str _menuItemName, str _dataSourceName)
{
    Args args = new Args();
    args.openMode(OpenMode::New);
    FormRun formRun = new MenuFunction(_menuItemName, MenuItemType::Display).create(args);
    formRun.run();

    // Tell the data source to create a new record
    formRun.dataSource(_dataSourceName).create();

    return formRun;
}

/// <summary>
/// Initializes the hierarchy viewer control.
/// </summary>
public void initControl()
{
    if (!build)
    {
        build = HierarchyViewer.build();
    }

    formController = DirViewLocationHierarchyController::construct(this as Object);

    if (build)
    {
        HierarchyViewer.initControl(FormController);
    }
}

private void setRootInHierarchy(int64 _nodeId = formController.getRootID())
{
    HierarchyViewer.parmRootNodeId(_nodeId);
}

// Method part of the DirViewLocationHierarchyUIInterface
public void activeRecordChanged(Common _activeRecord)
{
    DataArea    dataAreaChange;

    // Change the company context to the company of the selected node
    if (dataArea.Id != formController.activeDataArea())
    {
        appl.setDefaultCompany(formController.activeDataArea(), false);

        // Set the data area record to ensure dynalinked forms will create the correct link
        select crossCompany dataAreaChange where dataAreaChange.Id == formController.activeDataArea();
        dataArea.data(dataAreaChange);
    }

    // Change the contents of the preview pane to match the selection
    wrkCtrTablePreviewTab.visible(false);
    wrkCtrTableGroupPreviewTab.visible(false);
    prodUnitPreviewTab.visible(false);
    wmsAislePreviewTab.visible(false);
    wmsLocationPreviewTab.visible(false);
    inventLocationPreviewTab.visible(false);
    sitePreviewTab.visible(false);
    dataAreaPreviewTab.visible(false);

    switch (_activeRecord.TableId)
    {
        case tableNum(WrkCtrTable):
            wrkCtrTableIndividual.data(_activeRecord);
            wrkCtrTablePreviewTab.visible(true);
            wrkCtrTableIndividual_ds.refresh();
            wrkCtrTableIndividual_WrkCtrType.update();
            wrkCtrTableIndividual_EffectivityPct.update();
            wrkCtrTableIndividual_OperationSchedPct.update();
            wrkCtrTableIndividual_Capacity.update();
            break;

        case tableNum(WrkCtrResourceGroup):
            wrkCtrResourceGroup.data(_activeRecord);
            wrkCtrTableGroup.data(wrkCtrResourceGroup.wrkCtrTable());
            wrkCtrTableGroupPreviewTab.visible(true);
            wrkCtrResourceGroup_ds.refresh();
            wrkCtrTableGroup_ds.refresh();
            wrkCtrTableGroup_EffectivityPct.update();
            wrkCtrTableGroup_OperationSchedPct.update();
            break;

        case tableNum(ProdUnitTable):
            prodUnitTable.data(_activeRecord);
            prodUnitPreviewTab.visible(true);
            prodUnitTable_ds.refresh();
            prodUnitTable_InputInventLocationId.update();
            prodUnitTable_OutputInventLocationId.update();
            wrkCtrTableGroup_Capacity.update();
            break;

        case tableNum(WMSAisle):
            wmsAisle.data(_activeRecord);
            wmsAislePreviewTab.visible(true);
            wmsAisle_ds.refresh();
            wmsAisle_aisleNo.update();
            wmsAisle_sortCode.update();
            wmsAisle_sortDescending.update();
            break;

        case tableNum(WMSLocation):
            wmsLocation.data(_activeRecord);
            wmsLocationPreviewTab.visible(true);
            wmsLocation_ds.refresh();
            wmsLocation_position.update();
            wmsLocation_level.update();
            wmsLocation_rack.update();
            break;

        case tableNum(InventLocation):
            inventLocation.data(_activeRecord);
            logisticsPostalAddress.data(LogisticsLocationEntity::location2PostalAddress(InventLocation::getFirstLocation(inventLocation.RecId), DateTimeUtil::getSystemDateTime()));
            inventLocationPreviewTab.visible(true);
            inventLocation_ds.refresh();
            logisticsPostalAddress_ds.refresh();
            inventLocationPreviewTab.update();
            break;

        case tableNum(InventSite):
            inventSite.data(_activeRecord);
            logisticsPostalAddress.data(LogisticsLocationEntity::location2PostalAddress(InventSite::getLocationFromRole(inventSite.RecId, LogisticsLocationRoleType::Delivery)));
            sitePreviewTab.visible(true);
            inventSite_ds.refresh();
            logisticsPostalAddress_ds.refresh();
            sitePreviewTab.update();

            // Handle dimension display
            boolean showFinancialDimension = InventParameters::find().SiteDimensionAttribute;
            DimensionEntryControl.visible(showFinancialDimension);
            if (showFinancialDimension)
            {
                DimensionEntryControl.parmCompany(inventSite.company());
                DimensionEntryControl.reactivate();
            }
            break;

        case tableNum(DataArea):
            dataArea.data(_activeRecord);
            companyInfo.data(CompanyInfo::findDataArea(dataArea.Id));
            ledger.data(Ledger::findByLegalEntity(companyInfo.RecId));
            logisticsPostalAddress.data(companyInfo.postalAddress());
            dataAreaPreviewTab.visible(true);
            dataArea_ds.refresh();
            companyInfo_ds.refresh();
            ledger_ds.refresh();
            logisticsPostalAddress_ds.refresh();
            dataAreaPreviewTab.update();
            break;
    }
}

