public boolean askBeforeRecordDelete(Common _common)
{
    return (Box::yesNo(strFmt("@SYS302359", tableId2pname(_common.TableId)), DialogButton::No) == DialogButton::Yes);
}

public void changeCompanyIfNecessary()
{
    if (formController.activeDataArea() != curext())
    {
        appl.setDefaultCompany(formController.activeDataArea(), false);
    }
}

