public void refreshTree()
{
    HierarchyViewer.applyBuild();
    element.setRootInHierarchy();
    formController.reportSelectedNode(formController.getRootID());
}

public void run()
{
    super();

    formController.reportSelectedNode(formController.getRootID());
}

// Method part of the DirViewLocationHierarchyUIInterface
public void showRecordForm(Common _common)
{
    str         menuItemStr;
    boolean     findRecord  = true;
    Args        args        = new Args();
    FormRun     formRun;

    // Find out which menu item to open
    switch (_common.TableId )
    {
        case tableNum(DataArea):
            menuItemStr = menuitemDisplayStr(OMLegalEntities);
            findRecord = false;
            break;
        case tableNum(InventSite):
            menuItemStr = menuitemDisplayStr(InventSite);
            break;
        case tableNum(InventLocation):
            menuItemStr = menuitemDisplayStr(InventLocations);
            break;
        case tableNum(WMSAisle):
            menuItemStr = menuitemDisplayStr(WMSAisle);
            break;
        case tableNum(WMSLocation):
            menuItemStr = menuitemDisplayStr(WMSLocation);
            break;
        case tableNum(ProdUnitTable):
            menuItemStr = menuitemDisplayStr(ProdUnitTable);
            break;
        case tableNum(WrkCtrResourceGroup):
            menuItemStr = menuitemDisplayStr(WrkCtrResourceGroup);
            break;
        case tableNum(WrkCtrTable):
            menuItemStr = menuitemDisplayStr(WrkCtrTable);
            break;
    }

    // Change to the selected company and open the menu item
    if (menuItemStr && Global::hasMenuItemAccess(menuItemStr, MenuItemType::Display))
    {
        changecompany(formController.activeDataArea())
        {
            args.caller(this);
            if (findRecord)
            {
                args.lookupRecord(_common);
            }
            formRun = new MenuFunction(menuItemStr, MenuItemType::Display).create(args);
            formRun.run();
        }
    }
}

