/// <summary>
/// Invokes the action associated with the button.
/// </summary>
void clicked()
{
    FormRun         formRun;

    super();

    // Open the menu item
    Args args = new Args();
    args.lookupRecord(inventSite);

    formRun = new MenuFunction(menuitemDisplayStr(InventSite), MenuItemType::Display).create(args);
    formRun.run();
}

/// <summary>
/// Invokes the action associated with the button.
/// </summary>
void clicked()
{
    FormRun             formRun;
    FormTabPageControl  tabPageControl;

    super();

    // Open the menu item
    Args args = new Args();
    args.lookupRecord(inventLocation);
    formRun = new MenuFunction(menuitemDisplayStr(InventLocations), MenuItemType::Display).create(args);
    formRun.run();

    // Set focus on the address tab
    tabPageControl = formRun.design().controlName(formControlStr(InventLocation,TabAddress));
    tabPageControl.expand();
}

void clicked()
{
    Args args;

    // No call to super as the arguments must be set to the active record buffer first
    args = new Args();
    args.record(formController.getSelectedRecord());
    MenuFunction::runCalled(menuitemActionStr(WMSAisleCopy), MenuItemType::Action, false, args);
}

/// <summary>
/// Initialize the HierarchyViewer control
/// </summary>
public void applyBuild()
{
    super();

    element.initControl();

    if (build)
    {
        this.initControl(formController);
    }
}
