Method.Source:
[Form]
public class EGAISWaybillJour_RU extends FormRun
{
    EGAISDocumentDirection_RU           direction;
    EGAISWaybillJour_RU                 callerBuffer;
    boolean                             isIncoming, isOutgoing;

}

public void init()
{
    if (   !element.args()
        || element.args().parmEnumType() != enumNum(EGAISDocumentDirection_RU))
    {
        throw error("@SYS22996");            
    }

    if (element.args().dataset() == tableNum(EGAISWaybillJour_RU))
    {
        callerBuffer = element.args().record();
    }

    direction = element.args().parmEnum();
    isIncoming = (direction == EGAISDocumentDirection_RU::Incoming);
    isOutgoing = (direction == EGAISDocumentDirection_RU::Outgoing);

    super();

    element.initDesign();
}

public void initDesign()
{
    Overview_M_unsettledQty.visible(isIncoming);
    RegistrationGroup.visible(isIncoming);
    EGAISWaybillSettlement_RU.visible(isIncoming);
    EGAISWaybillJour_RU_ds.object(fieldNum(EGAISWaybillJour_RU, StatusReceipt)).visible(isIncoming);
    EGAISWaybillJour_RU_ds.object(fieldNum(EGAISWaybillJour_RU, StatusIssue)).visible(isOutgoing);
    EGAISWaybillLine_RU_ds.object(fieldNum(EGAISWaybillLine_RU, RealQty)).visible(isOutgoing);
    TabPageEGAIS.visible(isOutgoing);

    // Generate
    EGAISPurchCreate_RU.visible(isIncoming);
    EGAISWaybillCreate_RU.visible(isOutgoing);
    EGAISRequestRepealWBCreate_RU.visible(isIncoming);
    EGAISWaybillPost_ProductReceipt_RU.visible(isIncoming);
    EGAISWaybillPost_Invoice_RU.visible(isIncoming);
    EGAISReturnOrderCreate_RU.visible(isOutgoing);

    // Journals
    EGAISWaybillActJourIncoming_RU.visible(isOutgoing);
    EGAISRequestRepealWBOutgoing_RU.visible(isIncoming);
    EGAISOutgoingDocument_RU.visible(isOutgoing);
    VendPackingSlipJournal.visible(isIncoming);
    VendInvoiceJournal.visible(isIncoming);
    SourceCustInvoiceJournal.visible(isOutgoing);
    ReturnTableDetails.visible(isOutgoing);
    ReturnCustPackingSlipJournal.visible(isOutgoing);
    ReturnCustInvoiceJournal.visible(isOutgoing);
}

public void setTitleText()
{
    headerTitle.text(strFmt('%1 : %2 - %3', EGAISWaybillJour_RU.WaybillNum,
                                            EGAISWaybillJour_RU.shipper().RegId,
                                            EGAISWaybillJour_RU.shipper().FullName));
}

public void updateDesign()
{
    boolean     existsOutgoingWaybillAct = (select firstOnly EGAISWaybillActJour_RU
                                                where EGAISWaybillActJour_RU.WaybillJour == EGAISWaybillJour_RU.RecId
                                                   && EGAISWaybillActJour_RU.Direction   == EGAISDocumentDirection_RU::Outgoing).RecId;

    boolean     existsIncomingWaybillAct = (select firstOnly EGAISWaybillActJour_RU
                                                where EGAISWaybillActJour_RU.WaybillJour == EGAISWaybillJour_RU.RecId
                                                   && EGAISWaybillActJour_RU.Direction   == EGAISDocumentDirection_RU::Incoming).RecId;

    boolean     existsOutgoingRequestRepeal = (select firstOnly EGAISRequestRepealWB_RU
                                                   where EGAISRequestRepealWB_RU.WaybillJour == EGAISWaybillJour_RU.RecId
                                                      && EGAISRequestRepealWB_RU.Direction   == EGAISDocumentDirection_RU::Outgoing).RecId;

    EGAISWaybillJour_RU_ds.allowDelete(EGAISWaybillJour_RU.canBeDeleted());
    EGAISWaybillJour_RU_ds.allowEdit(EGAISWaybillJour_RU.canBeEdited());
    EditDetailsButton.enabled(EGAISWaybillJour_RU.canBeEdited());
    EGAISWaybillRegister_RU.enabled(EGAISWaybillJour_RU.canBeRegistered());
    EGAISWaybillRegisterCancel_RU.enabled(EGAISWaybillJour_RU.canRegistrationBeCancelled());
    EGAISWaybillJour_RU_ds.object(fieldNum(EGAISWaybillJour_RU, Supplier)).visible(EGAISWaybillJour_RU.Supplier);
    EGAISWaybillJour_RU_ds.object(fieldNum(EGAISWaybillJour_RU, UnitType)).visible(EGAISWaybillJour_RU.Version == EGAISDocumentVersion_RU::Version1);

    // Generate
    EGAISPurchCreate_RU.enabled(EGAISWaybillJour_RU.canPurchBeCreated());
    EGAISWaybillActCreate_RU.enabled(EGAISWaybillJour_RU.canWaybillActBeCreated());
    EGAISRequestRepealWBCreate_RU.enabled(EGAISWaybillJour_RU.canRequestRepealWBBeCreated());
    EGAISWaybillPost_ProductReceipt_RU.enabled(EGAISWaybillJour_RU.canProductReceiptBePosted());
    EGAISWaybillPost_Invoice_RU.enabled(EGAISWaybillJour_RU.canInvoiceBePosted());
    EGAISReturnOrderCreate_RU.enabled(EGAISWaybillJour_RU.canReturnOrderBeCreated());

    // Journals
    EGAISTTNInformBRegJour_RU.enabled(EGAISWaybillJour_RU.RegId ? true : false);
    EGAISOutgoingDocument_RU.enabled(EGAISOutgoingDocument_RU::existSourceRecId(EGAISWaybillJour_RU.RecId, EGAISDocumentType_RU::Waybill));
    EGAISTicket_RU.enabled(EGAISTicket_RU::existSourceRecId(EGAISWaybillJour_RU.RecId, EGAISDocumentType_RU::Waybill));
    EGAISWaybillActJourOutgoing_RU.enabled(existsOutgoingWaybillAct);
    EGAISWaybillActJourIncoming_RU.enabled(existsIncomingWaybillAct);
    EGAISRequestRepealWBOutgoing_RU.enabled(existsOutgoingRequestRepeal);
    VendPackingSlipJournal.enabled(EGAISWaybillJour_RU.VendPackingSlipJour);
    VendInvoiceJournal.enabled(EGAISWaybillJour_RU.VendInvoiceJour);
    SourceCustInvoiceJournal.enabled(EGAISWaybillJour_RU.SourceCustInvoiceJour);
    ReturnTableDetails.enabled(EGAISWaybillJour_RU.ReturnOrderSalesId != '');
    ReturnCustPackingSlipJournal.enabled(EGAISWaybillJour_RU.ReturnCustPackingSlipJour);
    ReturnCustInvoiceJournal.enabled(EGAISWaybillJour_RU.ReturnCustInvoiceJour);    
}

public int active()
{
    int ret;

    ret = super();

    element.updateDesign();

    element.setTitleText();

    return ret;
}

public void init()
{
    super();

    this.query().dataSourceTable(tableNum(EGAISWaybillJour_RU)).addRange(fieldNum(EGAISWaybillJour_RU, Direction)).value(queryValue(direction));

    if (callerBuffer)
    {
        this.query().dataSourceTable(tableNum(EGAISWaybillJour_RU)).addRange(fieldNum(EGAISWaybillJour_RU, RecId)).value(queryValue(callerBuffer.RecId));
    }
}

public int active()
{
    int ret;

    ret = super();

    EGAISInformA.enabled(EGAISWaybillLine_RU.informB().InformA);
    EGAISInformB.enabled(EGAISWaybillLine_RU.InformB);

    return ret;
}

void clicked()
{
    Args                 args;
    QueryBuildDataSource queryBuildDataSource;
    FormDataSource       formDataSource;
    QueryBuildRange      queryBuildRange;
    FormRun              formRunCustInvoiceJour;

    if (!EGAISWaybillJour_RU.SourceCustInvoiceJour)
    {
        return;
    }

    args = new Args(formStr(CustInvoiceJournal));
    args.caller(this);

    formRunCustInvoiceJour = classfactory.formRunClass(args);
    formRunCustInvoiceJour.init();

    formDataSource       = formRunCustInvoiceJour.dataSource();

    queryBuildDataSource = formDataSource.query().dataSourceTable(tableNum(CustInvoiceJour));

    queryBuildRange      = queryBuildDataSource.addRange(fieldNum(CustInvoiceJour, RecId));
    queryBuildRange.value(queryValue(EGAISWaybillJour_RU.SourceCustInvoiceJour));

    formRunCustInvoiceJour.run();
    formRunCustInvoiceJour.wait();
}

