[Form]
public class EGAISWaybillSettlement_RU extends FormRun
{
    EGAISWaybillJour_RU     waybillJour;
    EGAISWaybillLine_RU     waybillLine;
    InventDimCtrl_Frm       inventDimFormSetup;

}

    public void applyRanges()
    {
        QueryBuildDataSource        qbdsPurchLine;
        QueryBuildDataSource        qbdsInventDim;
        QueryBuildDataSource        qbdsVendTable;
        QueryBuildDataSource        qbdsSettlement;
        EGAISAlcoholProduction_RU   alcoholProduction = waybillLine.alcoholProduction();

        qbdsPurchLine = PurchLine_ds.query().dataSourceTable(tableNum(PurchLine));
        SysQuery::findOrCreateRange(qbdsPurchLine, fieldNum(PurchLine, ItemId)).value(alcoholProduction.ItemId);
        SysQuery::findOrCreateRange(qbdsPurchLine, fieldNum(PurchLine, PurchUnit)).value(alcoholProduction.Unit);
        qbdsSettlement = PurchLine_ds.query().dataSourceTable(tableNum(EGAISWaybillSettlement_RU));

        if (element.isSettlementMode())
        {
            // we should see purchLines with non-zero RemainPurchPhysical only during settlement, otherwise we should see only settled purchLines
            SysQuery::findOrCreateRange(qbdsPurchLine, fieldNum(PurchLine, RemainPurchPhysical)).value(SysQuery::valueNot(0));
            qbdsSettlement.enabled(false);
        }
        else
        {
            SysQuery::findOrCreateRange(qbdsPurchLine, fieldNum(PurchLine, RemainPurchPhysical)).value(SysQuery::valueUnlimited());
            qbdsSettlement.enabled(true);
            qbdsSettlement.addRange(fieldNum(EGAISWaybillSettlement_RU, WaybillLine)).value(queryValue(waybillLine.RecId));
        }

        qbdsInventDim = PurchLine_ds.query().dataSourceTable(tableNum(InventDim));
        SysQuery::findOrCreateRange(qbdsInventDim, fieldNum(InventDim, ConfigId)).value(queryValue(alcoholProduction.ConfigId));
        SysQuery::findOrCreateRange(qbdsInventDim, fieldNum(InventDim, InventSizeId)).value(queryValue(alcoholProduction.InventSizeId));
        SysQuery::findOrCreateRange(qbdsInventDim, fieldNum(InventDim, InventColorId)).value(queryValue(alcoholProduction.InventColorId));
        SysQuery::findOrCreateRange(qbdsInventDim, fieldNum(InventDim, InventStyleId)).value(queryValue(alcoholProduction.InventStyleId));
		SysQuery::findOrCreateRange(qbdsInventDim, fieldNum(InventDim, InventVersionId)).value(queryValue(alcoholProduction.InventVersionId));

        qbdsVendTable = PurchLine_ds.query().dataSourceTable(tableNum(VendTable));
        SysQuery::findOrCreateRange(qbdsVendTable, fieldNum(VendTable, Party)).value(queryValue(waybillJour.shipper().party));

        PurchLine_ds.executeQuery();
    }

public void close()
{
    Object      caller;

    super();

    if (   element.args()
        && element.args().caller()
        && element.args().caller().name() == formStr(EGAISWaybillJour_RU))

    {
            caller = element.args().caller();
            caller.updateDesign();
    }
}

public void init()
{
    if (   !element.args().dataset()
        || element.args().dataset() != tableNum(EGAISWaybillLine_RU))
    {
            throw error(strFmt("@SYS22678",element.name()));
    }

    super();

    element.updateDesign(InventDimFormDesignUpdate::Init);

    element.updateCtrl();
}

public boolean isSettlementMode()
{
    return waybillJour.StatusReceipt == EGAISWaybillStatusReceipt_RU::Received
        && (select firstOnly EGAISWaybillActJour_RU
              where EGAISWaybillActJour_RU.WaybillJour == waybillJour.RecId
                    && EGAISWaybillActJour_RU.Direction   == EGAISDocumentDirection_RU::Outgoing
                    && (   EGAISWaybillActJour_RU.StatusIssue == EGAISWaybillActStatusIssue_RU::Created
                        || EGAISWaybillActJour_RU.StatusIssue == EGAISWaybillActStatusIssue_RU::Sent
                        || EGAISWaybillActJour_RU.StatusIssue == EGAISWaybillActStatusIssue_RU::Accepted)).RecId == 0;
}

public void updateCtrl()
{
    boolean allowEdit   = waybillJour.canBeRegistered();
    boolean allowDelete = element.isSettlementMode();

    waybillLine.reread();
    AlcCode.text(waybillLine.alcoholProduction().AlcCode);
    LineQty.realValue(waybillLine.Qty);
    UnsettledQty.realValue(waybillLine.unsettledQty());
    EGAISWaybillSettlement_RU_ds.research(true);
    PurchLine_ds.research(true);
    SettleButton.enabled(UnsettledQty.realValue() != 0 && PurchLine.EGAISUnsettledQty_RU() && allowDelete);

    RemoveButton.enabled(allowDelete);
    EGAISWaybillSettlement_RU_ds.allowEdit(allowEdit);
    EGAISWaybillSettlement_RU_ds.allowDelete(allowDelete);
}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm   inventDimParmShow;
    InventDimParm   inventDimParmEnabled;

    switch (_mode)       
    {
        case InventDimFormDesignUpdate::Init,
                InventDimFormDesignUpdate::LinkActive  :

            if (! inventDimFormSetup)

            {
                inventDimFormSetup  = InventDimCtrl::newFromFormValidDataSource(element, InventDimSettlement_ds.name());                       
            }
            InventDimGroupSetup::newInventTable(InventTable::find(waybillLine.itemId())).inventDimParmActive(inventDimParmShow);
            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParmShow);
            // No break
        case InventDimFormDesignUpdate::Active:
            InventDimGroupSetup::newInventTable(InventTable::find(waybillLine.itemId())).inventDimParmActive(inventDimParmEnabled);
            inventDimParmEnabled.InventBatchIdFlag = false;     // should not be editable
            inventDimFormSetup.parmDimParmEnabled(inventDimParmEnabled);
            inventDimFormSetup.formSetControls(true);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

public void init()  
{
    QueryBuildDataSource        qbdsPurchLine;
    QueryBuildDataSource        qbdsSettlement;
    QueryBuildDataSource        qbdsInventDim;
    QueryBuildDataSource        qbdsPurchTable;
    QueryBuildDataSource        qbdsVendTable;

    super();

    qbdsPurchLine = PurchLine_ds.query().dataSourceTable(tableNum(PurchLine));

    qbdsSettlement = qbdsPurchLine.addDataSource(tableNum(EGAISWaybillSettlement_RU));
    qbdsSettlement.relations(true);
    qbdsSettlement.joinMode(JoinMode::ExistsJoin);
    qbdsSettlement.enabled(false);

    qbdsInventDim = qbdsPurchLine.addDataSource(tableNum(InventDim));
    qbdsInventDim.relations(true);
    qbdsInventDim.joinMode(JoinMode::ExistsJoin);

    qbdsPurchTable = qbdsPurchLine.addDataSource(tableNum(PurchTable));
    qbdsPurchTable.relations(true);
    qbdsPurchTable.joinMode(JoinMode::ExistsJoin);

    qbdsVendTable = qbdsPurchTable.addDataSource(tableNum(VendTable));
    qbdsVendTable.addLink(fieldNum(PurchTable, InvoiceAccount), fieldNum(VendTable, AccountNum));
    qbdsVendTable.joinMode(JoinMode::ExistsJoin);
}

public int active()
{
    int ret;

    ret = super();

    SettleButton.enabled(UnsettledQty.realValue() != 0 && PurchLine.EGAISUnsettledQty_RU() && element.isSettlementMode());

    return ret;
}

public int active()
{
    int ret;

    ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active);

    return ret;            
}

public void linkActive()
{
    waybillLine = element.args().record();
    waybillJour = waybillLine.waybillJour();

    element.applyRanges();
    element.updateCtrl();

    element.updateDesign(InventDimFormDesignUpdate::LinkActive);

    super();
}

public void write()
{
    EGAISWaybillSettlement_RU.InventDimId = InventDim::findOrCreate(InventDimSettlement).InventDimId;

    super();

    element.updateCtrl();            
}

public void clicked()
{
    super();

    if (EGAISWaybillSettlement_RU::createPurchSettlement(PurchLine, waybillLine))
    {
        element.updateCtrl();
    }
}
