private void enableButtons()
{
    EInvoiceCFDIJournalHelper helper = EInvoiceCFDIJournalHelper::construct(EInvoiceJour);

    btnResubmitCFDI.enabled(helper.allowResubmission());
    btnCancelCFDI.enabled(helper.allowCancel());
    btnCancelCFDIManual.enabled(helper.allowManualCancel());
    btnSendMail.enabled(helper.allowEmailSending());
    btngrpPrint.enabled(helper.allowPrinting());
    btnSetConfirmNumber.enabled(helper.allowSetConfirmNumber());

    boolean allowXML = helper.allowXMLButton();
    EInvoiceXMLViewer_MX.enabled(allowXML);
    btnExportXML.enabled(allowXML);

    boolean isInvoicingServiceIntegrationEnabled = BusinessDocumentParameters::isFeatureEnabled([EInvoicingFeaturesForService::MX00010]);
    boolean isBusinessDocumentSubmissionDetails = helper.allowBusinessDocumentSubmissionDetails(ProjInvoiceJour.TableId, ProjInvoiceJour.RecId);
    BusinessDocumentSubmissionDetails.visible(isInvoicingServiceIntegrationEnabled || isBusinessDocumentSubmissionDetails);
    BusinessDocumentSubmissionDetails.enabled(isBusinessDocumentSubmissionDetails);
    EInvoiceHist_MX.enabled(!isBusinessDocumentSubmissionDetails);
}

public void init()
{
    super();

    QueryBuildDataSource invoiceQueryDataSource = ProjInvoiceJour_DS.query().dataSourceTable(tablenum(ProjInvoiceJour));

    invoiceQueryDataSource.addSortField(fieldnum(ProjInvoiceJour, InvoiceDate), SortOrder::Descending);
    invoiceQueryDataSource.addSortField(fieldnum(ProjInvoiceJour, ProjInvoiceId), SortOrder::Descending);
}

public int active()
{
    int ret;

    ret = super();

    element.enableButtons();

    return ret;
}

public void init()
{
    super();

    EInvoiceJour_DS.query().dataSourceTable(tablenum(EInvoiceJour_MX)).addRange(fieldNum(EInvoiceJour_MX, CFDI)).value(SysQuery::value(NoYes::Yes));

    allowEditFieldsOnFormDS_W(this, false);
    this.object(fieldNum(EInvoiceJour_MX, PrintInMST)).allowEdit(true);
}

public void clicked()
{
    super();

    for (
        Common common = EInvoiceJour_DS.getFirst(true) ? EInvoiceJour_DS.getFirst(true) : EInvoiceJour_DS.cursor();
        common;
        common = EInvoiceJour_DS.getNext()
    )
    {
        EInvoiceDocState_MX::construct(common).resubmitEInvoice();
    }

    EInvoiceJour_DS.research(true);
}

public void clicked()
{
    super();

    for (
        Common common = EInvoiceJour_DS.getFirst(true) ? EInvoiceJour_DS.getFirst(true) : EInvoiceJour_DS.cursor();
        common;
        common = EInvoiceJour_DS.getNext()
    )
    {
        EInvoiceDocState_MX::construct(EInvoiceJour).cancelEInvoice(true);
    }

    EInvoiceJour_DS.research(true);
}

public void clicked()
{
    EInvoiceJour_MX     eInvoiceJourLocal;

    super();

    eInvoiceJourLocal = EInvoiceJour_DS.getFirst(true) ? EInvoiceJour_DS.getFirst(true) : EInvoiceJour_DS.cursor();

    while (eInvoiceJourLocal)
    {
        if (eInvoiceJourLocal.CFDIStatus == EInvoiceCFDIStatus_MX::Approved)
        {
            EInvoiceCFDIMailSender_MX::construct(eInvoiceJourLocal).sendMail(true);
        }

        eInvoiceJourLocal = EInvoiceJour_DS.getNext();
    }

    EInvoiceJour_DS.research(true);
}
