public int active()
{
    int ret;

    ret = super();

    element.enableButtons();

    return ret;
}

public void init()
{
    super();

    EInvoiceJour_DS.query().dataSourceTable(tablenum(EInvoiceJour_MX)).addRange(fieldNum(EInvoiceJour_MX, CFDI)).value(SysQuery::value(NoYes::Yes));

    allowEditFieldsOnFormDS_W(this, false);
    this.object(fieldNum(EInvoiceJour_MX, PrintInMST)).allowEdit(true);
}

public int active()
{
    int ret;

    ret = super();

    if (WithholdingJour.WithholdingTypeCode == EInvoiceCFDIJournalHelper::RIFTaxpayersWithholdingType()
        && EInvoiceCFDIJournalHelper::construct(EInvoiceJour).allowDelete())
    {
        allowEditFieldsOnFormDS_W(WithholdingJour_ds, false);
        WithholdingJour_ds.object(fieldNum(CFDIWithholdingJour_MX, BimonthlyProfitAmount)).allowEdit(true);
        WithholdingJour_ds.object(fieldNum(CFDIWithholdingJour_MX, ISRCorrepondingAmount)).allowEdit(true);
    }
    else
    {
        WithholdingJour_ds.allowEdit(false);
    }

    return ret;
}

public void write()
{
    CFDIWithholdingJour_MX withholdingJourOrig = WithholdingJour.orig();

    super();

    if (withholdingJourOrig.BimonthlyProfitAmount != WithholdingJour.BimonthlyProfitAmount
        || withholdingJourOrig.ISRCorrepondingAmount != WithholdingJour.ISRCorrepondingAmount)
    {
        EInvoiceDocState_MX::construct(EInvoiceJour).resubmitEInvoice();
        EInvoiceJour_ds.reread();
    }
}

public void clicked()
{
    super();

    for (Common common = EInvoiceJour_DS.getFirst(true) ? EInvoiceJour_DS.getFirst(true) : EInvoiceJour_DS.cursor();
    common;
    common = EInvoiceJour_DS.getNext())
    {
        EInvoiceDocState_MX::construct(common).resubmitEInvoice();
    }

    EInvoiceJour_DS.research(true);
}

public void clicked()
{
    super();

    for (Common common = EInvoiceJour_DS.getFirst(true) ? EInvoiceJour_DS.getFirst(true) : EInvoiceJour_DS.cursor();
         common;
         common = EInvoiceJour_DS.getNext())
    {
        EInvoiceDocState_MX::construct(common).cancelEInvoice(true);
    }

    EInvoiceJour_DS.research(true);
}

public void clicked()
{
    super();

    EInvoiceJour_MX eInvoiceJourLocal = EInvoiceJour_DS.getFirst(true) ? EInvoiceJour_DS.getFirst(true) : EInvoiceJour_DS.cursor();

    while (eInvoiceJourLocal)
    {
        if (eInvoiceJourLocal.CFDIStatus == EInvoiceCFDIStatus_MX::Approved)
        {
            EInvoiceCFDIMailSender_MX::construct(eInvoiceJourLocal).sendMail(true);
        }

        eInvoiceJourLocal = EInvoiceJour_DS.getNext();
    }

    EInvoiceJour_DS.research(true);
}

public void clicked()
{
    if (WithholdingJour.RecId && Box::yesNo("@MexicoCFDIWithholding:DeleteJournalContinue_text", DialogButton::No, "@MexicoCFDIWithholding:WithholdingJour") == DialogButton::Yes)
    {
        if (CFDIWithholdingJour_MX::deleteJournal(WithholdingJour.RecId))
        {
            EInvoiceJour_DS.research(true);
        }
    }
}
