void enableButtons()
{
    EInvoiceProcessor_IT processor = EInvoiceProcessor_IT::newEInvoice(EInvoiceCust_IT);
    Set actionsApplicable = processor.getActionsApplicableToEInvoiceList();

    CustCreateUpdate.enabled(actionsApplicable.in(EInvoiceAction_IT::CreateXMLUpdate));
    CustSignUpdate.enabled(actionsApplicable.in(EInvoiceAction_IT::SignXMLUpdate));
}

/// <summary>
/// Returns the <c>CustInvoiceJour</c> table buffer.
/// </summary>
/// <returns>
/// The <c>CustInvoiceJour</c> table buffer.
/// </returns>
public Common docCursor()
{
    return CustInvoiceJour;
}

public int active()
{
    int ret = super();

    element.enableButtons();

    return ret;
}

public void init()
{
    QueryBuildDatasource qbds;

    super();

    qbds = CustInvoiceJour_DS.query().dataSourceTable(tablenum(CustInvoiceJour));
    qbds.addSortField(fieldNum(CustInvoiceJour, InvoiceId));
    qbds.addSortField(fieldNum(CustInvoiceJour, InvoiceDate));
}
