public void init()
{
    super();

    if (!(EInvoiceParameters_MX::isElectronicInvoiceEnabled() || EInvoiceCFDIParameters_MX::isElectronicInvoiceEnabled()))
    {
        throw error("@SYS341184");
    }
}

public void init()
{
    super();

    projInvoiceJour_DS.query().dataSourceNo(1).addSortField(fieldNum(ProjInvoiceJour, InvoiceDate), SortOrder::Descending);
    projInvoiceJour_DS.query().dataSourceNo(1).addSortField(fieldNum(ProjInvoiceJour, ProjInvoiceId), SortOrder::Descending);
}

