/// <summary>
/// Runs when the Command Button is clicked
/// </summary>
public void clicked()
{
    super();

    EInvoiceCFDIJournalHelper::construct(eInvoice_Sales_DS.cursor()).sendEInvoiceXmlToUser();
}

public void clicked()
{
    EInvoiceJour_MX     eInvoiceJourLocal;

    super();

    eInvoiceJourLocal = eInvoice_Sales_DS.getFirst(true) ? eInvoice_Sales_DS.getFirst(true) : eInvoice_Sales_DS.cursor();

    while (eInvoiceJourLocal)
    {
        EInvoiceMailSender_MX::construct(eInvoiceJourLocal).sendMail();
        eInvoiceJourLocal = eInvoice_Sales_DS.getNext();
    }
}

